import React, { useState, useEffect } from "react";
import { Carousel, Container, Image } from "react-bootstrap";
import { Row, Col } from "react-bootstrap";
import IconsKeyInfo from "../components/vehicleRelated/vehicleDetails/VehicleDetailsIcons.js";
import StickyDetails from "../components/vehicleRelated/vehicleDetails/StickyDetails.js";
import { Form, FormGroup } from "react-bootstrap";
import Select from 'react-select';


const imageUrls = [
  "https://placehold.co/720x400?text=Vehicle+Image",
  "https://placehold.co/720x400?text=Vehicle+Image2",
  "https://placehold.co/720x400?text=Vehicle+Image3",
  "https://placehold.co/720x400?text=Vehicle+Image4",
  "https://placehold.co/720x400?text=Vehicle+Image5",
];

const carouselStyle = {
  maxWidth: "720px", // Largeur maximale de l'image
  maxHeight: "400px", // Hauteur maximale de l'image
  margin: "auto", // Centre le carrousel si l'écran est plus large
};


const vehicleTypesOptions = [
  { value: 'chocolate', label: 'Chocolate' },
  { value: 'strawberry', label: 'Strawberry' },
  { value: 'vanilla', label: 'Vanilla' },
];



const vehicleOptions = [
  { value: 'addNewVehicleOptions', label: 'Ajouter une option de véhicule' },
  { value: 'strawberry', label: 'Strawberry' },
  { value: 'vanilla', label: 'Vanilla' },
];




export const VehicleDetailsForm = () => {
  const [selectedOption, setSelectedOption] = useState(null);
  //For Carrousel

  const [index, setIndex] = useState(0);
  const handleSelect = (selectedIndex, e) => {
    setIndex(selectedIndex);
  };


  //For VehicleOptions

  const [brands, setBrands] = useState([]);
  useEffect(() => {
    const fetchBrands = async () => {
      try {
        const response = await fetch("http://localhost:5001/api/brands");
        const data = await response.json();
        setBrands(data);
      } catch (error) {
        console.error("Error fetching brands:", error);
      }
    };
    fetchBrands();
  }, []);







  //For Multiple Select
  const [selectedOptions, setSelectedOptions] = useState([]);

  // Fonction pour gérer les changements de sélection
  const handleSelectChange = (event) => {
    // Récupération des options sélectionnées
    const selected = Array.from(
      event.target.selectedOptions,
      (option) => option.value
    );
    setSelectedOptions(selected);
  };

  return (
    <>
      <Container
        style={{ position: "relative", width: "39%", height: "2000px" }}
      >
        <Carousel
          activeIndex={index}
          onSelect={handleSelect}
          fade
          style={carouselStyle}
        >
          {imageUrls.map((url, idx) => (
            <Carousel.Item key={idx}>
              <Image
                className="d-block"
                src={url}
                alt={`Slide ${idx + 1}`}
                fluid
              />
            </Carousel.Item>
          ))}
        </Carousel>
        <div className="d-flex justify-content-center mt-0 ">
          {imageUrls.map((thumbnailUrl, idx) => (
            <Image
              key={`thumb-${idx}`}
              src={thumbnailUrl}
              alt={`Thumbnail ${idx + 1}`}
              className={`img-thumbnail ${
                index === idx ? "active-carousel-thumbnail" : ""
              } m-1 bg-transparent`}
              onClick={() => setIndex(idx)}
              style={{
                cursor: "pointer",
                maxWidth: "100px",
                maxHeight: "100px",
                marginRight: "4px",
                border: "none",
                outline: "none",
                borderRadius: "8px",
              }}
              thumbnail
            />
          ))}
        </div>
        <div className="justify">
          <p className="text-light mt-2">Réfèrence : fesfsefr</p>
          <p className="text-light mt-2">En ligne : 08/05/2023</p>
        </div>
        <div className="d-flex justify-content-center mt-2">
          <Container className="p-0">
            <div className="detail-panel-wrapper px-5 py-3 mb-3">
              <h2 className="details-title pb-4">Informations clès</h2>

              <Row>
                <Col
                  xs={12}
                  md={6}
                  className="px-4"
                >
                  <div className="d-flex align-items-top">
                    <IconsKeyInfo.Factory className="me-2 key-icons" />
                    <div className="px-2">
                      <FormGroup>
                        <Form.Label>
                          <h3 className="key-info-title">Marque et modèle :</h3>
                        </Form.Label>
                        <div className="">
                        <Form.Select aria-label="Select Brand">
                          <option>Choississez une marque</option>
                          <option value="addBrand">Ajouter une Marque</option>
                        </Form.Select>
                        <Form.Select aria-label="Select Model">
                          <option>Choississez un modèle</option>
                          <option value="addModel">Ajouter un Modèle</option>
                        </Form.Select>
                        </div>
                      </FormGroup>
                    </div>
                  </div>

                  <div className="d-flex align-items-top">
                    <IconsKeyInfo.Key className="me-2 key-icons" />
                    <div className="px-2">
                      <FormGroup>
                        <Form.Label>
                          <h3 className="key-info-title">
                            Mise en circulation :
                          </h3>
                        </Form.Label>
                        <Form.Control
                          type="number"
                          placeholder="Année de mise en circulation"
                        />
                      </FormGroup>
                    </div>
                  </div>

                  <div className="d-flex align-items-top">
                    <IconsKeyInfo.Mileage className="me-2 key-icons" />
                    <div className="px-2">
                      <FormGroup>
                        <Form.Label>
                          <h3 className="key-info-title">Kilomètrage :</h3>
                        </Form.Label>
                        <Form.Control
                          type="number"
                          placeholder="Kilometrage"
                          id="mileageForm"
                        />
                      </FormGroup>
                    </div>
                  </div>

                  <div className="d-flex align-items-top">
                    <IconsKeyInfo.Fuel className="me-2 key-icons" />
                    <div className="px-2">
                      <FormGroup>
                        <Form.Label>
                          <h3 className="key-info-title">Carburant :</h3>
                        </Form.Label>
                        <Form.Select aria-label="Select Fuel">
                          <option>Choississez un Carburant</option>
                          <option value="addModel">
                            Ajouter un Type de Carburant{" "}
                          </option>
                        </Form.Select>
                      </FormGroup>
                    </div>
                  </div>

                  <div className="d-flex align-items-top">
                    <IconsKeyInfo.Transmission className="me-2 key-icons" />
                    <div className="px-2">
                      <FormGroup>
                        <Form.Label>
                          <h3 className="key-info-title">Transmission :</h3>
                        </Form.Label>
                        <Form.Select aria-label="Select Transmission">
                          <option>Choississez une transmission</option>
                          <option value="addTransmission">
                            Ajouter une transmission
                          </option>
                        </Form.Select>
                      </FormGroup>
                    </div>
                  </div>
                </Col>

                <Col
                  xs={12}
                  md={6}
                  className="px-4"
                >
                  <div className="d-flex align-items-top">
                    <IconsKeyInfo.Stars className="me-2 key-icons" />
                    <div className="px-2">
                      <FormGroup>
                        <Form.Label>
                          <h3 className="key-info-title">Types :</h3>
                        </Form.Label>
                        <Select
                            defaultValue={selectedOption}
                            onChange={setSelectedOption}
                            options={vehicleTypesOptions}
                            isMulti
                            isSearchable
                          />
                    
                      </FormGroup>
                    </div>
                  </div>

                  <div className="d-flex align-items-top">
                    <IconsKeyInfo.Painting className="me-2 key-icons" />
                    <div className="px-2">
                      <h3 className="key-info-title">Peinture :</h3>
                      <p className="key-info">Rouge</p>
                    </div>
                  </div>

                  <div className="d-flex align-items-top">
                    <IconsKeyInfo.HorsePower className="me-2 key-icons" />
                    <div className="px-2">
                      <h3 className="key-info-title">Chevaux fiscaux :</h3>
                      <p className="key-info">9</p>
                    </div>
                  </div>

                  <div className="d-flex align-items-top">
                    <IconsKeyInfo.Door className="me-2 key-icons" />
                    <div className="px-2">
                      <FormGroup>
                        <Form.Label>
                          <h3 className="key-info-title">Portes :</h3>
                        </Form.Label>
                        <Form.Control
                          type="number"
                          placeholder="Nombre de porte "
                          id="doorsNumberForm"
                        />
                      </FormGroup>
                    </div>
                  </div>

                  <div className="d-flex align-items-top">
                    <IconsKeyInfo.Seat className="me-2 key-icons" />
                    <div className="px-2">
                      <FormGroup>
                        <Form.Label>
                          <h3 className="key-info-title">Sièges :</h3>
                        </Form.Label>
                        <Form.Control
                          type="number"
                          placeholder="Nombre de sièges "
                          id="seatsNumberForm"
                        />
                      </FormGroup>
                    </div>
                  </div>
                </Col>
              </Row>
            </div>
            <div className="detail-panel-wrapper px-5 py-3 mb-3">
              <h2 className="details-title pb-4">Options et équipements</h2>

              <Row>
          
                  <div className="d-flex align-items-top ">
                  <Select
                            defaultValue={selectedOption}
                            onChange={setSelectedOption}
                            options={vehicleTypesOptions}
                            isMulti
                            isSearchable
                          />
                  </div>
                
              </Row>
            </div>
          </Container>
        </div>
        <StickyDetails />
      </Container>
    </>
  );
};

export default VehicleDetailsForm;
