import React, { useEffect, useState } from 'react';
import { scheduleGetWeek } from "../../serverRelated/ApiRequest";
import { Row, Col } from "react-bootstrap";

const getCurrentDate = () => {
  const now = new Date();
  return {
    year: now.getFullYear(),
    weekNumber: now.getDate() - now.getDay() + (now.getDay() === 0 ? -6 : 1),
  };
};

export const FooterSchedule = () => {
  const [schedule, setSchedule] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      const currentDate = getCurrentDate();
      try {
        const year = currentDate.year;
        const weekNumber = currentDate.weekNumber;

        const response = await scheduleGetWeek(year, weekNumber, 1);
        const weekData = response[weekNumber];
        if (weekData) {
          const transformedWeekData = weekData.map(day => {
            const transformTime = time => time === null ? "Fermé" : (time.length > 5 ? time.slice(0, -3) : time);
            return {
              ...day,
              morningOpening: transformTime(day.morningOpening),
              morningClosing: transformTime(day.morningClosing),
              afternoonOpening: transformTime(day.afternoonOpening),
              afternoonClosing: transformTime(day.afternoonClosing),
            };
          });
          setSchedule(transformedWeekData);
        } else {
          setSchedule([]);
        }
      } catch (error) {
        console.error("Erreur lors de la récupération des horaires:", error);
      }
    };

    fetchData();
  }, []);

  return (
    <div>
      {schedule.length > 0 ? (
        <Row>
          {schedule.map((day, index) => (
            <Col key={index}>
              <li>Matin : {day.morningOpening} - {day.morningClosing}</li>
              <li>Après-midi : {day.afternoonOpening} - {day.afternoonClosing}</li>
            </Col>
          ))}
        </Row>
      ) : (
        <p>Aucun horaire disponible</p>
      )}
    </div>
  );
};

export default FooterSchedule;
