import React, { useState, useEffect } from "react";
import { Modal, Button, Row } from "react-bootstrap";
import CreatableSelect from "react-select/creatable";
import Select from "react-select";
import { capitalizeFirstLetter } from "../../functions/capitalizeFirstLetter";
import Form from "react-bootstrap/Form";

const ColorOption = (props) => {
    return (
      <div 
        style={{ display: 'flex', alignItems: 'center' }} 
        onClick={() => props.selectOption(props.data)}
      >
        <div 
          style={{
            width: '20px',
            height: '20px',
            backgroundColor: props.data.hex,
            marginLeft: '10px',
            marginRight: '10px',
          }} 
        />
        {props.data.label}
      </div>
    );
  };
  

const AddColorModal = ({ show, onHide, onModalClose }) => {
    const [selectedColor, setSelectedColor] = useState(null);
    const [colors, setColors] = useState([]);
    const [colorHex, setColorHex] = useState("#563d7c"); // Valeur initiale par défaut
  
    const handleModifyColorPicker = (e) => {
        setColorHex(e.target.value);
    };


  const handleCloseModal = () => {
    onHide();
    onModalClose();
  };

  const fetchColors = async () => {
    try {
      const response = await fetch("http://localhost:5001/api/colors");
      const data = await response.json();

      const transformedColors = data.map((color) => ({
        value: color.id.toString(),
        label: capitalizeFirstLetter(color.name),
        hex: color.hex, // Ajoutez ceci pour utiliser la couleur dans le composant d'option personnalisé
      }));

      setColors(transformedColors);
    } catch (error) {
      console.error("Error fetching colors:", error);
    }
  };

  useEffect(() => {
    fetchColors();
  }, []);

  const handleColorChange = (selectedOption) => {
    setSelectedColor(selectedOption);
    if (selectedOption) {
      setColorHex(selectedOption.hex); // Mettez à jour l'hexadécimal lors de la sélection d'une couleur
    } else {
      setColorHex("#563d7c"); // Remettre à la valeur par défaut si aucune couleur n'est sélectionnée
    }
  };

  return (
    <Modal
      show={show}
      onHide={onHide}
    >
      <Modal.Header closeButton>
        <Modal.Title>Titre du modal</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <Row>
            <CreatableSelect
            value={selectedColor}
            onChange={handleColorChange} // Utilisez la nouvelle fonction pour gérer le changement
            options={colors}
            isSearchable
            components={{ Option: ColorOption }} 
          />
          <Form.Control
            type="color"
            id="ColorInput"
            value={colorHex} // Utilisez l'état pour contrôler la valeur  
            onChange={handleModifyColorPicker}   
          />
          <Form.Control
            type="text-number"
            placeholder="Code hex"
            id="hex"
            disabled
            readOnly
            value={colorHex}
            />

            </Row>
      </Modal.Body>
      <Modal.Footer>
        <Button
          variant="primary"
        >
          Ajouter
        </Button>
        <Button
          variant="orange"
        >
          Modifier
        </Button>
        <Button
          variant="danger"
        >
          Supprimer
        </Button>
        <Button
          variant="secondary"
          onClick={handleCloseModal}
        >
          Fermer
        </Button>
      </Modal.Footer>
    </Modal>
  );
};

export default AddColorModal;
