import React, { useState } from 'react';
import  Accordion from 'react-bootstrap';


const VehicleFilters = () => {
  const [openKeys, setOpenKeys] = useState([]);

  const toggleAccordionItem = (key) => {
    setOpenKeys(prevOpenKeys =>
      prevOpenKeys.includes(key)
        ? prevOpenKeys.filter(openKey => openKey !== key)
        : [...prevOpenKeys, key]
    );
  };

  return (
    <Accordion>
      {[...Array(3)].map((_, index) => {
        const eventKey = index.toString();
        return (
          <Card key={eventKey}>
            <Card.Header>
              <Accordion.Toggle
                as={Card.Header}
                variant="link"
                eventKey={eventKey}
                onClick={() => toggleAccordionItem(eventKey)}
              >
                Filter {index + 1}
              </Accordion.Toggle>
            </Card.Header>
            <Accordion.Collapse eventKey={eventKey} in={openKeys.includes(eventKey)}>
              <Card.Body>
                {/* Your filter components go here */}
                Content for filter {index + 1}
              </Card.Body>
            </Accordion.Collapse>
          </Card>
        );
      })}
    </Accordion>
  );
};

export default VehicleFilters;
