import React, { useState, useEffect } from "react";
import { Form } from "react-bootstrap";
import Select from "react-select";

const vehicleOptions = [
  { value: "addNewVehicleOptions", label: "Ajouter une option de véhicule" },
  { value: "strawberry", label: "Strawberry" },
  { value: "vanilla", label: "Vanilla" },
];

export const Test = () => {
  const [selectedOption, setSelectedOption] = useState(null);

  const [options, setOptions] = useState(vehicleOptions); // Initialisation avec vehicleOptions

  useEffect(() => {
    const fetchOptions = async () => {
      try {
        const response = await fetch("http://localhost:5001/api/options");
        const data = await response.json();

        // Transformer les données reçues pour qu'elles correspondent au format de vehicleOptions
        const transformedOptions = data.map(option => ({
          value: option.id.toString(), // Assurez-vous que la valeur est une chaîne
          label: option.name,
        }));

        // Fusionner les options existantes avec les nouvelles
        setOptions(prevOptions => [...prevOptions, ...transformedOptions]);
      } catch (error) {
        console.error("Error fetching options:", error);
      }
    };
    fetchOptions();
  }, []);

  return (
    <>
      <Form.Label>
        <h3 className="key-info-title">Types :</h3>
      </Form.Label>
      <Select
        value={selectedOption}
        onChange={setSelectedOption}
        options={options} // Utilisation de l'état options
        isMulti
        isSearchable
      />
    </>
  );
};

export default Test;
