import React from "react";
import Container from "react-bootstrap/Container";
import Carousel from "react-bootstrap/Carousel";
import Placeholder from "react-bootstrap/Placeholder";
import Image from "react-bootstrap/Image";

const imageUrls = [
  "https://placehold.co/600x400?text=Vehicle+Image",
  "https://placehold.co/600x400?text=Vehicle+Image2",
  "https://placehold.co/600x400?text=Vehicle+Image3",
  "https://placehold.co/600x400?text=Vehicle+Image4",
  "https://placehold.co/600x400?text=Vehicle+Image5"
];

const MyCarouselWithThumbnails = () => 
{
  const [index, setIndex] = useState(0);

  const handleSelect = (selectedIndex, e) => {
    setIndex(selectedIndex);
  };

  return (
    <Container className="text-center">
     
      <Carousel activeIndex={index} onSelect={handleSelect} fade>
        {imageUrls.map((url, idx) => (
          <Carousel.Item key={idx}>
            <img
              className="d-block w-100"
              src={url}
              alt={`Slide ${idx + 1}`}
            />
          </Carousel.Item>
        ))}
      </Carousel>
      
      <div className="d-flex justify-content-center mt-4">
        {imageUrls.map((thumbnailUrl, idx) => (
          <img
            key={idx}
            src={thumbnailUrl}
            alt={`Thumbnail ${idx + 1}`}
            className={`img-thumbnail ${index === idx ? 'active' : ''}`}
            onClick={() => setIndex(idx)}
            style={{ cursor: 'pointer', maxWidth: '100px', maxHeight: '100px', marginRight: '4px' }}
          />
        ))}
      </div>
      <Container className="text-start"></Container>
    </Container>
  );
};
export default VehicleDetails;
