import React, { useState, useEffect } from "react";
import { Form } from "react-bootstrap";

import Select, { components } from "react-select";
import { capitalizeFirstLetter } from "../../../../functions/capitalizeFirstLetter";
import GUIIcons from "../../../general/GUIIcons";
import Button from "react-bootstrap/Button";

import AddColorModal from "../../../addingEntries/AddColorModal";

const ColorOption = (props) => {
  return (
    <components.Option {...props}>
      <GUIIcons.Square className="color-square-icon" style={{ fill: props.data.hex }} />
      {props.data.label}
    </components.Option>
  );
};

export const ColorForm = () => {
  const [showModal, setShowModal] = useState(false);
  const handleOpenModal = () => setShowModal(true);
  const handleCloseModal = () => setShowModal(false);
  const [selectedColor, setSelectedColor] = useState(null);
  const [colors, setColors] = useState([]);

  const fetchColors = async () => {
    try {
      const response = await fetch("http://localhost:5001/api/colors");
      const data = await response.json();

      const transformedColors = data.map(color => ({
        value: color.id.toString(),
        label: capitalizeFirstLetter(color.name),
        hex: color.hex, // Ajoutez ceci pour utiliser la couleur dans le composant d'option personnalisé
      }));

      setColors(transformedColors);
    } catch (error) {
      console.error("Error fetching colors:", error);
    }
  };

  useEffect(() => {
    
    fetchColors();
  }, []);

  return (
    <>
      <Form.Label>
        <h3 className="key-info-title">Couleurs :</h3>
      </Form.Label>
      <Select
  value={selectedColor}
  onChange={setSelectedColor}
  options={colors}
  isSearchable
/>

<Button onClick={handleOpenModal}>Ajouter une marque</Button>
      <AddColorModal
        show={showModal}
        onHide={handleCloseModal}
      />
    </>
  );
};

export default ColorForm;
