import React, { useState } from "react";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import axios from "axios"; // Assurez-vous d'installer axios avec `npm install axios`

export const AdminConnexionPanel = ({ show, onHide}) => {
  const [email, setEmail] = useState(""); // État pour l'email
  const [password, setPassword] = useState(""); // État pour le mot de passe

  const handleSubmit = async (event) => {
    event.preventDefault(); // Empêche le comportement par défaut du formulaire

    try {
      // Remplacer 'url_de_votre_api' par l'URL de votre API
      const response = await axios.post(
        "http://localhost:5001/api/admin/login",
        { email, password }
      );
      console.log(response.data); // Traiter la réponse ici
      // Vous pouvez rediriger l'utilisateur ou afficher un message de succès
    } catch (error) {
      console.error(
        "Erreur de connexion : ",
        error.response ? error.response.data : error.message
      );
      // Afficher un message d'erreur à l'utilisateur
    }
  };


  return (
    <Modal show={show} onHide={onHide}>
    <Modal.Header closeButton>
      <Modal.Title>Connexion</Modal.Title>
    </Modal.Header>
    <Modal.Body>
    



    
    </Modal.Body>
    <Modal.Footer>
      <Button variant="secondary" onClick={onHide}> Fermer </Button>
    </Modal.Footer>
  </Modal>
    
  );

}
export default AdminConnexionPanel;
