import React, { useState, useEffect } from "react";
import { Button, Form } from "react-bootstrap";
import Select from "react-select";
import { capitalizeFirstLetter } from "../../../../functions/capitalizeFirstLetter";
import AddBrandModal from "../../../addingEntries/AddBrandModal";

// Renommez cette variable pour éviter les conflits
let initialBrands = [
  // Ajoutez une option pour "Créer une nouvelle entrée"
  { value: "new", label: "Créer une nouvelle entrée" },
];

export const BrandForm = ({ onBrandChange }) => {
  const [showModal, setShowModal] = useState(false);
  const handleOpenModal = () => setShowModal(true);
  const handleCloseModal = () => setShowModal(false);

  const [selectedBrand, setSelectedBrand] = useState(null);
  const [brands, setBrands] = useState(initialBrands); // Utilisez le nom renommé ici

  useEffect(() => {
    const fetchBrands = async () => {
      try {
        const response = await fetch("http://localhost:5001/api/brands");
        const data = await response.json();

        const transformedBrands = data.map((brand) => ({
          value: brand.id.toString(),
          label: capitalizeFirstLetter(brand.name),
        }));

        // Ajoutez l'option "Créer une nouvelle entrée" aux marques transformées
        transformedBrands.unshift({
          value: "new",
          label: "Créer une nouvelle entrée",
        });

        setBrands(transformedBrands); // Modifiez pour remplacer complètement les marques
      } catch (error) {
        console.error("Error fetching options:", error);
      }
    };
    fetchBrands();
  }, []);

  const handleBrandChange = (selectedOption) => {
    setSelectedBrand(selectedOption);
    if (onBrandChange) {
      onBrandChange(selectedOption); // Envoyer l'objet sélectionné au parent
    }
  };

  return (
    <>
      <Select
        value={selectedBrand}
        onChange={handleBrandChange}
        options={brands}
        isSearchable
      />
      <AddBrandModal show={showModal} onHide={handleCloseModal} />
    </>
  );
};

export default BrandForm;
