export function handleDateDifference(jsonDate) {
    let currentDate = new Date();
    const parsedDate = new Date(jsonDate);
  
    const timeDifference = currentDate - parsedDate;
    const secondsDifference = Math.floor(timeDifference / 1000);
    const minutesDifference = Math.floor(secondsDifference / 60);
    const hoursDifference = Math.floor(minutesDifference / 60);
    const daysDifference = Math.floor(hoursDifference / 24);
    const monthsDifference = Math.floor(daysDifference / 30.4375); // Moyenne de jours par mois
    const yearsDifference = Math.floor(monthsDifference / 12);
  
    if (monthsDifference < 1) { // Moins d'un mois, afficher le nombre de jours
      return `${daysDifference} jour(s)`;
    } else if (monthsDifference < 12) { // Entre 1 mois et moins d'un an, afficher le nombre de mois
      return `${monthsDifference} mois`;// Plus d'un an, afficher le nombre d'années
    } else {
      return `${yearsDifference} an(s)`;
    }
  }
