import React, { useState, useEffect } from 'react';
import Table from 'react-bootstrap/Table';
import { scheduleGetAllDays, scheduleGetWeek } from '../../../serverRelated/ApiRequest';

export const ScheduleManagement = () => {
  const [preparedData, setPreparedData] = useState([]);

  // Fonction pour préparer les données pour l'affichage
  const prepareDataForDisplay = (data) => {
    const groupedData = [];
    let currentGroup = [];
  
    data.forEach(day => {
      currentGroup.push(day);
      if (currentGroup.length === 7) {
        groupedData.push(currentGroup);
        currentGroup = [];
      }
    });
  
    if (currentGroup.length > 0) {
      groupedData.push(currentGroup);
    }
  
    return groupedData;
  };

  useEffect(() => {
    const fetchData = async () => {
      const year = getCurrentYear(); // Utiliser l'année actuelle
      try {
        const daysData = await scheduleGetAllDays(year);
        const weekData = await scheduleGetWeek(10, year, 8); // Exemple de valeurs pour weekStart et weekNumber
        const allData = [...daysData, ...weekData]; // Supposition sur la fusion des données
        const prepared = prepareDataForDisplay(allData); // Préparer les données pour l'affichage
        setPreparedData(prepared);
      } catch (error) {
        console.error("Erreur lors de la récupération des horaires:", error);
      }
    };
    
    fetchData();
  }, []);

  const rowsHeaders = ["Date", "Ouverture Matin", "Fermeture Matin", "Ouverture Après-midi", "Fermeture Après-midi"];

  return (
    <>
      {preparedData.map((week, index) => (
        <Table key={index} striped bordered hover>
          <tbody>
            {rowsHeaders.map((header, headerIndex) => (
              <tr key={headerIndex}>
                <th>{header}</th>
                {week.map((day, dayIndex) => (
  <tr key={dayIndex}>
    <td>{day.date}</td>
    <td>{day.isOpen ? day.morningOpening || "Fermé" : "Fermé"}</td>
    <td>{day.isOpen ? day.morningClosing || "Fermé" : "Fermé"}</td>
    <td>{day.isOpen ? day.afternoonOpening || "Fermé" : "Fermé"}</td>
    <td>{day.isOpen ? day.afternoonClosing || "Fermé" : "Fermé"}</td>
  </tr>
))}

           {week.map((day, dayIndex) => (
  <tr key={dayIndex}>
    <td>{day.date}</td>
    <td>{day.isOpen ? day.morningOpening || "Fermé" : "Fermé"}</td>
    <td>{day.isOpen ? day.morningClosing || "Fermé" : "Fermé"}</td>
    <td>{day.isOpen ? day.afternoonOpening || "Fermé" : "Fermé"}</td>
    <td>{day.isOpen ? day.afternoonClosing || "Fermé" : "Fermé"}</td>
  </tr>
))}   </tr>
            ))}
          </tbody>
        </Table>
      ))}
    </>
  );
};

const getCurrentYear = () => {
  const currentDate = new Date();
  return currentDate.getFullYear();
};

export default ScheduleManagement;
