import { Button, NavLink } from "react-bootstrap";
import { useState } from "react";
import { AdminConnexionPanel } from "../admin/AdminConnexionPanel";
import AddAdminModal from '../addingEntries/AddAdminModal';


export const FooterComp = () => {
  const [showConnexionModal, setShowConnexionModal] = useState(false);
  const handleOpenConnexionModal = () => setShowConnexionModal(true);
  const handleCloseConnexionModal = () => setShowConnexionModal(false);

  const [showAddAdminModal, setShowAddAdminModal] = useState(false);
  const handleOpenAddAdminModal = () => setShowAddAdminModal(true);
  const handleCloseAddAdminModal = () => setShowAddAdminModal(false);

  return (
    <div className="text-center">
      <p>{garages.adress}</p>
      <p>{garages._code}</p>
      <p>{garages.phone}</p>
      <p>{garages.email}</p>
      <Button onClick={handleOpenConnexionModal}>Connexion</Button>
      <AdminConnexionPanel
        show={showConnexionModal}
        onHide={handleCloseConnexionModal}
      />
      <Button onClick={handleOpenAddAdminModal}>Ajouter un admin</Button>
      <AddAdminModal
        show={showAddAdminModal}
        onHide={handleCloseAddAdminModal}
      />
    </div>
  );
};
