import Form from 'react-bootstrap/Form';
import useState  from 'react';

export const UserRequest = () => {
// #region //* Handle phone Number 
  const [phoneNumber, setPhoneNumber] = useState('+336');

  const handlePhoneChange = (event) => {
    const input = event.target.value;
    // Assurez-vous que l'entrée commence toujours par '+336'
    if (!input.startsWith('+336')) {
      setPhoneNumber('+336' + input.slice(4));
    } else {
      setPhoneNumber(input);
    }
  };

// #endregion

    return (
        <>
        <Form>
      <div className="mb-3">
        <Form.Check
          inline
          label="M."
          name="gender"
          type="radio"
          id={`inline-radio-mr`}
        />
        <Form.Check
          inline
          label="Mme."
          name="gender"
          type="radio"
          id={`inline-radio-md`}
        />
      
      <Form.Group className="mb-3">
        <Form.Label>Nom</Form.Label>
        <Form.Control type="text" placeholder="Nom" required/>

        <Form.Label>Adresse Email</Form.Label>
        <Form.Control type="email" placeholder="adresse@mail.fr" />
      
        <Form.Label>Numéro de téléphone</Form.Label>
        <Form.Control 
        type="phone"
        value={phoneNumber}
        onChange={handlePhoneChange}/>
      
      </Form.Group>
      
      </div>




    </Form>
        </>
    )
}

export default UserRequest;
