import Form from "react-bootstrap/Form";
import React, { useState, useEffect } from "react";
import InputGroup from "react-bootstrap/InputGroup";

export const UserRequest = () => {
  // #region //* Handle phone Number
  const [phoneNumber, setPhoneNumber] = useState("+336");

  const handlePhoneChange = (event) => {
    let input = event.target.value;
    if (input.startsWith("+336")) {
      input = "+336" + input.slice(4).replace(/[^0-9]/g, "");
    } else {
      input = "+336";
    }

    if (input.length > 13) {
      input = input.slice(0, 13);
    }
    setPhoneNumber(input);
  };

  // #endregion

  return (
    <>
      <Form className="text-light px-1 py-3">
        <div className="mb-3">
          <Form.Check
            inline
            label="M."
            name="gender"
            type="radio"
            id={`inline-radio-mr`}
          />
          <Form.Check
            inline
            label="Mme."
            name="gender"
            type="radio"
            id={`inline-radio-md`}
          />

          <Form.Group className="mb-3">
            <Form.Label>Nom</Form.Label>
            <Form.Control
              type="text"
              placeholder="Nom"
              required
            />

            <Form.Label>Adresse Email</Form.Label>
            <Form.Control
              type="email"
              placeholder="adresse@mail.fr"
            />

            <Form.Label>Numéro de téléphone</Form.Label>
            <Form.Control
              type="phone"
              value={phoneNumber}
              onChange={handlePhoneChange}
              required
            />
            <Form.Control.Feedback type="invalid">
              Entrez un numero de téléphone valide
            </Form.Control.Feedback>
          </Form.Group>

          <Form.Check
            inline
            label="Préfèrer être contacté par email"
            name="gender"
            type="checkbox"
            id={`inline-checkbox-md`}
          />

          <Form.Control
            type="text"
            placeholder="Réfèrence du véhicule"
            aria-label="Réfèrence du véhicule"
            disabled
            readOnly
          />

          <Form.Group
            className="mb-3"
            controlId="exampleForm.ControlTextarea1"
          >
            <Form.Label>Message</Form.Label>
            <Form.Control
              as="textarea"
              rows={5}
            />
          </Form.Group>
        </div>
      </Form>
    </>
  );
};

export default UserRequest;
