import Container from "react-bootstrap/Container";
import VehicleCard from "../components/VehicleCard.js";
import VehicleSearchBar from "../components/VehicleSearchBar.js";
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Form from 'react-bootstrap/Form';
import VehiclesCardrousel from "../sections/VehiclesCardrousel.js";
import Stack from 'react-bootstrap/Stack';
import VehicleFilters from "../components/VehicleFilters.js";
import {BsFillFunnelFill} from 'react-icons/bs';


export const Vehicles = () =>
{
    return (
        <main>
          
                <div className="mb-5 mt-5">
                <VehicleSearchBar/>
                </div>
          <div className="mb-5 mt-5 bg-dark">
            
            <Row className="">
            <Row className="pt-3 bg-primary d-flex justify-content-center align-items-center text-center">
     
                <h3 className="text-light mx-5">220 Véhicules trouvés</h3>
                <Form.Select aria-label="sort-select" className="sort-select">
                    <option>Trier par ...</option>
                    <option value="1">Prix croissant <i class="bi bi-funnel-fill"></i></option>
                    <option value="2">Prix décroissant</option>
                    <option value="3">Année croissante</option>
                    <option value="4">Année décroissante</option>
                    <option value="5">Km. croissant</option>
                    <option value="6">Km. décroissant</option>
   
                </Form.Select>
            </Row>

                <Col className="bg-light">
                <VehicleFilters className="float"/>
                </Col>
                <Col xs={8} className="d-flex justify-content-center">
                
                <Stack direction="horizontal" gap={5}>
                    <VehicleCard />
                    <VehicleCard />
                    <VehicleCard />
                    <VehicleCard />
                </Stack>
                </Col>
                <Col className="bg-light">3 of 3</Col>
            </Row>
            </div>
        </main>
    )
}

export default Vehicles;
