import React, { useState, useEffect } from "react";
import "../styles/custom.css";
import { fetchDisplayedTestimonials } from "../serverRelated/ApiRequest";

export const TestimonySect = () => {
  const [testimonies, setTestimonies] = useState([]);

  useEffect(() => {
    const getTestimonies = async () => {
      const data = await fetchDisplayedTestimonials();
      setTestimonies(data);
    };

    getTestimonies();
  }, []);

  return (
    <>
      {testimonies.map((testimony, index) => (
        <div key={index} className="bg-light testimony-card">
          <p>{testimony.name}</p>
          <p>{testimony.testimony}</p>
          <p>Note: {testimony.note}</p>
          <p>Date: {new Date(testimony.posted_at).toLocaleDateString()}</p>
        </div>
      ))}
    </>
  );
};

export default TestimonySect;
