import React, { useEffect, useState } from 'react'; // Ajout des imports nécessaires
import { scheduleGetWeek } from "../../serverRelated/ApiRequest"; // Import de la fonction de récupération des horaires 

// Fonction hypothétique getCurrentDate, à remplacer par votre implémentation réelle
const getCurrentDate = () => {
  const now = new Date();
  return {
    year: now.getFullYear(),
    weekStart: now.getDate() - now.getDay() + (now.getDay() === 0 ? -6 : 1), // Calcul simplifié pour l'exemple
  };
};

export const FooterSchedule = () => {
  const [schedule, setSchedule] = useState([]); // Ajout d'un état pour stocker les données de l'horaire

  useEffect(() => {
    const fetchData = async () => {
      const currentDate = getCurrentDate();
      console.log("currentDate", currentDate);
      try {
        
        // Assurez-vous que les fonctions renvoient directement un tableau ou extrayez le tableau comme nécessaire
        const daysResponse = await scheduleGetWeek(currentDate.year, currentDate.weekStart, 1);
        console.log("fetched schedule get week", daysResponse);
        setSchedule(daysResponse); // Stockage des données de l'horaire dans l'état
      } catch (error) {
        console.error("Erreur lors de la récupération des horaires:", error);
      }
    };

    fetchData();
  }, []);

  // Affichage des données de l'horaire dans l'UI
  return (
    <div>
      {schedule.length > 0 ? (
        <ul>
          {schedule.map((day, index) => (
            <li key={index}>{/* Utilisez ici les propriétés de `day` pour afficher les données */}</li>
          ))}
        </ul>
      ) : (
        <p>Aucun horaire disponible</p> // Message si l'horaire est vide
      )}
    </div>
  );
};

export default FooterSchedule;
