import React, { useState, useEffect } from "react";
import { Button, Form } from "react-bootstrap";
import Select from "react-select";
import { capitalizeFirstLetter } from "../../../..";
import AddOptionModal from "../../addingEntries/AddOptionModal";


let brands = [
 // { value: "id", label: "Brand name" },
];

export const BrandForm = () => {
  const [showModal, setShowModal] = useState(false);
  const handleOpenModal = () => setShowModal(true);
  const handleCloseModal = () => setShowModal(false);


  const [selectedBrand, setSelectedBrand] = useState(null);
  const [brands, setBrands] = useState(brands);

  useEffect(() => {
    const fetchBrands = async () => {
      try {
        const response = await fetch("http://localhost:5001/api/brands");
        const data = await response.json();

        // Transformer les données reçues pour qu'elles correspondent au format de vehicleOptions
        const transformedBrands = data.map(brand => ({
          value: brand.id.toString(), // Assurez-vous que la valeur est une chaîne
          label: capitalizeFirstLetter(brand.name),
        }));

        setBrands (prevOptions => [...prevOptions, ...transformedBrands]);
      } catch (error) {
        console.error("Error fetching options:", error);
      }
    };
    fetchBrands();
  }, []);

  return (
    <>
      <Form.Label>
        <h3 className="key-info-title">Types :</h3>
      </Form.Label>
      <Select
        value={selectedBrand}
        onChange={setSelectedBrand}
        options={brands}
        isMulti
        isSearchable
      />
    </>

  );
};

export default BrandForm;
