import { capitalizeFirstLetter } from "../../functions/capitalizeFirstLetter";
import React, { useState, useEffect } from "react";
import { Modal, Button } from 'react-bootstrap';
import Select from "react-select";
import { Form } from "react-bootstrap";
import Row from "react-bootstrap/Row";
import CreatableSelect from 'react-select/creatable';


const AddAdminModal = ({ show, onHide }) => {

   

    const handleCloseModal = () => {
        onHide();   
        onModalClose(); 
      };
// Renommez cette variable pour éviter les conflits
return (
    <Modal
    show={show}
    onHide={onHide}
  >
    <Modal.Header closeButton>
      <Modal.Title>Titre du modal</Modal.Title>
    </Modal.Header>
    <Modal.Body>
      <Row>
        <Form.Label>Nom</Form.Label>
        <Form.Control
          type="text"
          placeholder="Nom"
          id="name"
        />
        <Form.Label>Email</Form.Label>
        <Form.Control
          type="email"
          placeholder="Email"
          id="email"
        />
        <Form.Label>Mot de passe</Form.Label>
        <Form.Control
          type="password"
          placeholder="Mot de passe"
          id="password"
        />
        <Form.Label>Is master</Form.Label>
        <Form.Control
          type="checkbox"
          placeholder="Is master"
          id="is_master"
        />
      </Row>
    </Modal.Body>
    <Modal.Footer>
      <Button
        variant="primary"
        onClick="handleAddAdmin"
      >
        Ajouter
      </Button>
      <Button variant="orange">Modifier</Button>
      <Button variant="danger">Supprimer</Button>
      <Button
        variant="secondary"
        onClick={handleCloseModal}
      >
        Fermer
      </Button>
    </Modal.Footer>
  </Modal>

);
}

export default AddAdminModal;
