import React, { useState, useEffect } from "react";
import Table from "react-bootstrap/Table";
import Button from "react-bootstrap/Button";
import { fetchCustomerRequest } from "../../../serverRelated/ApiRequest";
import { formatDateToDmy } from '../../../functions/formatDateToDmy';
import VehicleDetails from "../../../screens/VehicleDetails";

export const RequestManagement = () => {
  const [requests, setRequests] = useState([]);
  const [selectedVehicleId, setSelectedVehicleId] = useState(null);

  useEffect(() => {
    const getCustomerRequests = async () => {
      try {
        const data = await fetchCustomerRequest();
        setRequests(data);
      } catch (error) {
        console.error("Erreur lors de la récupération des demandes client:", error);
      }
    };

    getCustomerRequests();
  }, []);

  const handleViewDetails = (vehicleId) => {
    setSelectedVehicleId(vehicleId);
  };

  return (
    <>
      <div className="bg-dark">
        <h1>RequestManagement</h1>

        <Table striped bordered hover>
          <thead>
            <tr>
              <th>Reçu il y a</th>
              <th>Statut</th>
              <th>Request ref</th>
              <th>Nom du client</th>
              <th>Vehicle ref</th>
              <th>Message</th>
              <th>Numéro de téléphone</th>
              <th>Email</th>
              <th>Préférence</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {requests.map((request, index) => (
              <tr key={index}>
                <td>{formatDateToDmy(request.received_at)}</td>
                <td>{request.is_processed ? "Traitée" : "En attente"}</td>
                <td>{request.id}</td>
                <td>{request.requester_name}</td>
                <td>
                  <Button onClick={() => handleViewDetails(request.vehicle_id)}>
                    Voir
                  </Button>
                </td>
                <td>{request.message}</td>
                <td>{request.phone}</td>
                <td>{request.email}</td>
                <td>{request.preferPhone ? "Téléphone" : "Email"}</td>
                <td>
                  {/* Boutons d'action */}
                </td>
              </tr>
            ))}
          </tbody>
        </Table>
        
        {selectedVehicleId && <VehicleDetails vehicleId={selectedVehicleId} />}
      </div>
    </>
  );
};

export default RequestManagement;
