import { useState } from "react";
import { Button } from "react-bootstrap";
 
export const Admin = () => {

  const [showAdminManagement, setShowAdminManagement] = useState(false);
  const handleOpenAdminManagement = () => setShowAdminManagement(true);

  const [showVehicleManagement, setShowVehicleManagement] = useState(false);
  const handleOpenVehicleManagement = () => setShowVehicleManagement(true);

  const [showVehicleRelatedManagement, setShowVehicleRelatedManagement] = useState(false);
  const handleOpenVehicleRelatedManagement = () => setShowVehicleRelatedManagement(true);

  const [showRequestManagement, setShowRequestManagement] = useState(false);
  const handleOpenRequestManagement = () => setShowRequestManagement(true);

  const [showTestimonyManagement, setShowTestimonyManagement] = useState(false);
  const handleOpenTestimonyManagement = () => setShowTestimonyManagement(true);




  return (
    <div>
      <Button
      onClick={handleOpenAdminManagement}>
        Manager les comptes admins
      </Button>
      <Button
      onClick={handleOpenVehicleManagement}>
        Manager les véhicules
      </Button>
      <Button
      onClick={handleOpenVehicleRelatedManagement}>
        Manager les données des véhicules
      </Button>
      <Button
      onClick={handleOpenRequestManagement}>
        Manager les demandes
      </Button>
      <Button
      onClick={handleOpenTestimonyManagement}>
        Manager les commentaires
      </Button>
    </div>
  );
}
