import React, { useState, useEffect } from "react";
import { Button, Form } from "react-bootstrap";
import Select from "react-select";
import { capitalizeFirstLetter } from "../../../../functions/capitalizeFirstLetter";
import AddVehicleModelModal from "../../../addingEntries/AddVehicleModelModal";

export const ModelForm = ({ onModelChange, brand_id }) => {
  const [showModal, setShowModal] = useState(false);
  const [selectedModel, setSelectedModel] = useState(null);
  const [models, setModels] = useState([]);

  const handleOpenModal = () => {
    setShowModal(true);
    setSelectedModel(null);
  };

  const handleChange = (selectedOption) => {
    setSelectedModel(selectedOption);
    onModelChange(selectedOption.value);
  };

  const handleCloseModal = () => setShowModal(false);

  const fetchModels = async () => {
    if (!brand_id) {
      console.log("Brand ID is not set");
      return;
    }

    try {
      const response = await fetch(`http://localhost:5001/api/vehicleModels/byBrand/${brand_id}`);
      const data = await response.json();

      const transformedModels = data.map(model => ({
        value: model.id.toString(),
        label: capitalizeFirstLetter(model.name),
        wheel_number: model.wheel_number,
        door_number: model.door_number,
        seat_number: model.seat_number,
      }));
      

      setModels(transformedModels);
    } catch (error) {
      console.error("Error fetching models:", error);
    }
  };

  useEffect(() => {
    fetchModels();
  }, [brand_id, showModal]); // Déclencher à nouveau lorsque showModal change pour rafraîchir la liste après ajout

  return (
    <>
      <Form.Label>
        <h3 className="key-info-title">Modèle :</h3>
      </Form.Label>
      <Select
        value={selectedModel}
        onChange={handleChange}
        options={models}
        isSearchable
        placeholder="Sélectionnez un modèle..."
      />
      <Button onClick={handleOpenModal}>Ajouter un modèle</Button>
      <AddVehicleModelModal
        show={showModal}
        onHide={handleCloseModal}
        onModalClose={fetchModels} // Rafraîchir les modèles après fermeture du modal
      />
    </>
  );
};

export default ModelForm;
