import React, { useState, useEffect } from "react";
import { Accordion } from "react-bootstrap";
import Container from "react-bootstrap/Container";
import Form from "react-bootstrap/Form";
import OverlayTrigger from "react-bootstrap/OverlayTrigger";
import Tooltip from "react-bootstrap/Tooltip";
import { capitalizeFirstLetter } from "../../functions/capitalizeFirstLetter";
import { fetchBrands, fetchVehicleModels, fetchVehicleTypes } from "../../serverRelated/ApiRequest";

const renderTooltip = (description) => (
  <Tooltip id="button-tooltip">{description}</Tooltip>
);

const VehicleFilters = () => {
  const [filters, setFilters] = useState({
    brands: [],
    vehicleModels: [],
    vehicleTypes: [],
  });

  
const [selectedBrands, setSelectedBrands] = useState([]);
const [selectedModels, setSelectedModels] = useState([]);
const [selectedTypes, setSelectedTypes] = useState([]);


  useEffect(() => {
    const initFilters = async () => {
      try {
        const brandsData = await fetchBrands();
        const vehicleModelsData = await fetchVehicleModels();
        const vehicleTypesData = await fetchVehicleTypes();
        setFilters({
          brands: brandsData,
          vehicleModels: vehicleModelsData,
          vehicleTypes: vehicleTypesData,
        });
      } catch (error) {
        console.error("Erreur lors de l'initialisation des filtres:", error);
      }
    };
    initFilters();
  }, []);

  const renderFilterOptions = (filterOptions, labelKey) => (
    filterOptions.map((option, index) => {
      const label = typeof option === 'string' ? option : option[labelKey] || '';
      return (
        <Form.Check
          key={index}
          label={`${capitalizeFirstLetter(label)}`}
          value={label}
          className="text-light"
        />
      );
    })
  );

  const handleBrandChange = (event) => {
    const brand = event.target.value;
    setSelectedBrands(
      event.target.checked
        ? [...selectedBrands, brand]
        : selectedBrands.filter(b => b !== brand)
    );
  };

  const handleModelChange = (event) => {
    const model = event.target.value;
    setSelectedModels(
      event.target.checked
        ? [...selectedModels, model]
        : selectedModels.filter(m => m !== model)
    );
  };

  const handleTypeChange = (event) => {
    const type = event.target.value;
    setSelectedTypes(
      event.target.checked
        ? [...selectedTypes, type]
        : selectedTypes.filter(t => t !== type)
    );
  };

  return (
    <Container className="bg-dark filters-wrapper" style={{ width: "20%" }}>
      <Accordion defaultActiveKey={["0"]} style={{"--bs-accordion-bg": "transparent", "--bs-accordion-border-color": "transparent"}} alwaysOpen>
        <Accordion.Item eventKey="0">
          <Accordion.Header className="filter-accordion-title">Filtre</Accordion.Header>
          <Accordion.Body>
            {/* Marques */}
            <Accordion defaultActiveKey={["0"]} style={{"--bs-accordion-bg": "transparent", "--bs-accordion-border-color": "transparent"}} alwaysOpen>
              <Accordion.Item eventKey="0">
                <Accordion.Header className="filter-accordion-sub-title">Marques</Accordion.Header>
                <Accordion.Body>
                  <Form.Control className="mb-3 filter-search-form m-auto" type="text" placeholder="Search" />
                  <Form className="px-3 filter-options-wrapper">
                    {renderFilterOptions(filters.brands, 'name')}
                  </Form>
                </Accordion.Body>
              </Accordion.Item>
            </Accordion>
            {/* Modèles */}
            <Accordion defaultActiveKey={["0"]} style={{"--bs-accordion-bg": "transparent", "--bs-accordion-border-color": "transparent"}} alwaysOpen>
              <Accordion.Item eventKey="0">
                <Accordion.Header className="filter-accordion-sub-title">Modèles</Accordion.Header>
                <Accordion.Body>
                  <Form.Control className="mb-3 filter-search-form m-auto" type="text" placeholder="Search" />
                  <Form className="px-3 filter-options-wrapper">
                    {filters.vehicleModels.map((model, index) => (
                      <OverlayTrigger key={index} placement="right" delay={{ show: 250, hide: 400 }} overlay={renderTooltip(model.description)}>
                        <Form.Check
                          key={index}
                          label={`${capitalizeFirstLetter(model.name)} (nb)`}
                          className="text-light"
                        />
                      </OverlayTrigger>
                    ))}
                  </Form>
                </Accordion.Body>
              </Accordion.Item>
            </Accordion>
             {/* Type */}
             <Accordion defaultActiveKey={["0"]} style={{"--bs-accordion-bg": "transparent", "--bs-accordion-border-color": "transparent"}} alwaysOpen>
              <Accordion.Item eventKey="0">
                <Accordion.Header className="filter-accordion-sub-title">Modèles</Accordion.Header>
                <Accordion.Body>
                  <Form.Control className="mb-3 filter-search-form m-auto" type="text" placeholder="Search" />
                  <Form className="px-3 filter-options-wrapper">
                    {filters.vehicleTypes.map((vehicleType, index) => (
                      <OverlayTrigger key={index} placement="right" delay={{ show: 250, hide: 400 }} overlay={renderTooltip(vehicleType.description)}>
                        <Form.Check
                          key={index}
                          onChange={handleTypeChange}
                          label={`${capitalizeFirstLetter(vehicleType.name)} (nb)`}
                          className="text-light"
                        />
                      </OverlayTrigger>
                    ))}
                  </Form>
                </Accordion.Body>
              </Accordion.Item>
            </Accordion>
          </Accordion.Body>
        </Accordion.Item>
      </Accordion>
    </Container>
  );
};

export default VehicleFilters;
