import React, { useState, useEffect } from "react";
import Table from "react-bootstrap/Table";
import Button from "react-bootstrap/Button";
import { fetchCustomerRequest } from "../../../serverRelated/ApiRequest";
import { formatDateToDmy } from '../../../functions/formatDateToDmy';

export const RequestManagement = () => {
  const [requests, setRequests] = useState([]);

  useEffect(() => {
    const getCustomerRequests = async () => {
      try {
        const data = await fetchCustomerRequest();
        // Trier les données
        const sortedData = data.sort((a, b) => {
          // Trier par statut (en attente en premier)
          if (!a.is_processed && b.is_processed) return -1;
          if (a.is_processed && !b.is_processed) return 1;

          // Ensuite, trier par date (la plus récente en premier)
          return new Date(b.received_at) - new Date(a.received_at);
        });
        setRequests(sortedData);
      } catch (error) {
        console.error(
          "Erreur lors de la récupération des demandes client:",
          error
        );
      }
    };

    getCustomerRequests();
  }, []);

  const handleViewDetails = (vehicleId) => {
    window.open(`/vehicleDetails/${vehicleId}`, '_blank');
  };

  return (
    <>
      <div className="bg-dark">
        <h1>RequestManagement</h1>

        <Table striped bordered hover>
          <thead>
            <tr>
              <th>Reçu il y a</th>
              <th>Statut</th>
              <th>Request ref</th>
              <th>Nom du client</th>
              <th>Vehicle ref</th>
              <th>Message</th>
              <th>Numéro de téléphone</th>
              <th>Email</th>
              <th>Préférence</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {requests.map((request, index) => (
              <tr key={index}>
                <td>{formatDateToDmy(request.received_at)}</td>
                <td>{request.is_processed ? "Traitée" : "En attente"}</td>
                <td>{request.id}</td>
                <td>{request.requester_name}</td>
                <td>
                  <Button onClick={() => handleViewDetails(request.vehicle_id)}>
                    Voir
                  </Button>
                </td>
                <td>{request.message}</td>
                <td><a href={`phoneto:${request.phone}`}>{request.phone}</a></td>
                <td><a href={`mailto:${request.email}`}>{request.email}</a></td>

                <td>{request.prefer_phone ? "Téléphone" : "Email"}</td>
                <td>
                 <Button onClick={handleProcessRequest(request.id)}>
                    Traiter
                  </Button>
                </td>
              </tr>
            ))}
          </tbody>
        </Table>
      </div>
    </>
  );
};

export default RequestManagement;
