import React, { useState, useEffect } from "react";
import { Container } from "react-bootstrap";
import GUIIcons from "../../general/GUIIcons";
import Offcanvas from "react-bootstrap/Offcanvas";
import { capitalizeFirstLetter } from "../../../functions/capitalizeFirstLetter";
import Button from "react-bootstrap/Button";
import { Tabs, Tab } from "react-bootstrap";
import Form from "react-bootstrap/Form";
import InputGroup from "react-bootstrap/InputGroup";

function StickyDetails({
  vehicleId,
  brand,
  vehicleModel,
  productionYear,
  mileage,
  fuelType,
  transmission,
  vehicleCondition,
  price,
  vehicleComment,
  vehicleModelDescription,
}) {
  const [showOffcanvasRequest, setShowOffcanvasRequest] = useState(false);

  const handleShowRequest = () => setShowOffcanvasRequest(true);
  const handleCloseRequest = () => setShowOffcanvasRequest(false);

  const [topValue, setTopValue] = useState(0);

  const handleScroll = () => {
    setTopValue(window.scrollY);
  };

  const [showOffcanvas, setShowOffcanvas] = useState(false);

  useEffect(() => {
    window.addEventListener("scroll", handleScroll);

    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  const [phoneNumber, setPhoneNumber] = useState("+336");
  const handlePhoneChange = (event) => {
    let input = event.target.value;
    if (input.startsWith("+336")) {
      input = "+336" + input.slice(4).replace(/[^0-9]/g, "");
    } else {
      input = "+336";
    }

    if (input.length > 13) {
      input = input.slice(0, 13);
    }
    setPhoneNumber(input);
  };

  //OffCanvas
  const [show, setShow] = useState(false);

  const [vehicleConditions, setVehicleConditions] = useState([]);
  useEffect(() => {
    const fetchVehicleConditions = async () => {
      try {
        const response = await fetch(
          "http://localhost:5001/api/vehicleConditions"
        );
        const data = await response.json();
        setVehicleConditions(data);
      } catch (error) {
        console.error("Error fetching vehicle conditions:", error);
      }
    };
    fetchVehicleConditions();
  }, []);

  return (
    <>
      <div
        style={{
          position: "absolute",
          top: `${topValue}px`,
          left: "1000px",
          width: "500px",
          height: "auto",
          transition: "all 0.5s ease",
        }}
        className="stick-info-wrapper"
      >
        <Container className="mt-2 mb-3">
          <h3 className="stick-info-title">
            {brand} - {vehicleModel}
          </h3>
          <p className="stick-infos">
            {productionYear} - {mileage} km
          </p>
          <p className="stick-infos">
            {fuelType} - {transmission}{" "}
          </p>
          <p
            className="stick-infos align-items-top"
            onClick={() => setShow(true)}
          >
            {" "}
            {vehicleCondition} <GUIIcons.Info className="info-icon" />
          </p>
        </Container>
        <div className="mb-5">
          <Tabs
            defaultActiveKey="condition-description"
            id="sticky-descriptions"
            className="bg-dark mt-4"
            fill
          >
            <Tab
              eventKey="condition-description"
              title="État du véhicule"
              className="bg-light pb-3 pt-3 px-3"
            >
              {vehicleComment}
            </Tab>
            <Tab
              eventKey="model-description"
              title="Commentaire du Modèle"
              className="bg-light pb-3 pt-3 px-3"
            >
              {vehicleModelDescription}
            </Tab>
          </Tabs>
        </div>
        <div className="d-flex flex-column justify-content-center align-items-center">
          <h4 className="stick-info-garantie">Garantie 12 mois</h4>
          <h3 className="stick-info-price">{price} €</h3>
          <h4 className="stick-info-garantie mt-4">
            Une question ? Un rendez-vous ?
          </h4>
          <Button
            className="btn btn-primary btn-lg mt-3"
            onClick={handleShowRequest}
          >
            Nous contacter
          </Button>
        </div>
      </div>

      <Offcanvas
        show={show}
        onHide={() => setShow(false)}
        className="bg-dark custom-offcanvas"
      >
        <Offcanvas.Header
          closeVariant="white"
          closeButton
        >
          <Offcanvas.Title className="mx-auto offcanvas-title">
            États des véhicules
          </Offcanvas.Title>
        </Offcanvas.Header>
        <Offcanvas.Body>
          <ul className="px-1">
            {vehicleConditions.map((condition, index) => (
              <div className="mb-3">
                <li
                  key={index}
                  className="text-light li-no-style"
                >
                  <span className="offcanvas-condition-name">
                    {" "}
                    {capitalizeFirstLetter(condition.name) + " : "}{" "}
                  </span>{" "}
                  <br />
                  <span className="offcanvas-condition-description">
                    {condition.description}
                  </span>
                </li>
                <hr />
              </div>
            ))}
          </ul>
        </Offcanvas.Body>
      </Offcanvas>

      <Offcanvas
        show={showOffcanvasRequest}
        onHide={handleCloseRequest}
        scroll={true}
        backdrop={false}
        className="bg-dark custom-offcanvas"
      >
        <Offcanvas.Header closeButton>
          <Offcanvas.Title>Contacter nous !</Offcanvas.Title>
        </Offcanvas.Header>
        <Offcanvas.Body>
          <Form className="text-light px-1 py-3">
            <div className="mb-3">
              <Form.Check
                inline
                label="M."
                name="gender"
                type="radio"
                id={`inline-radio-mr`}
              />
              <Form.Check
                inline
                label="Mme."
                name="gender"
                type="radio"
                id={`inline-radio-md`}
              />

              <Form.Group className="mb-3">
                <Form.Label>Nom</Form.Label>
                <Form.Control
                  type="text"
                  placeholder="Nom"
                  required
                />

                <Form.Label>Adresse Email</Form.Label>
                <Form.Control
                  type="email"
                  placeholder="adresse@mail.fr"
                />

                <Form.Label>Numéro de téléphone</Form.Label>
                <Form.Control
                  type="tel"
                  defaultValue={phoneNumber}
                  onChange={handlePhoneChange}
                />
                <Form.Control.Feedback type="invalid">
                  Entrez un numéro de téléphone valide
                </Form.Control.Feedback>
              </Form.Group>

              <Form.Check
                inline
                label="Préférer être contacté par email"
                name="preference"
                type="checkbox"
                id={`inline-checkbox-md`}
              />

              <Form.Group
                className="mb-3 mt-3"
                controlId=""
              >
                <Form.Label>Message</Form.Label>

                <InputGroup className="mb-3">
                  <InputGroup.Text id="vehicle-ref">
                    réf : {vehicleId}
                  </InputGroup.Text>
                </InputGroup>
                <Form.Control
                  as="textarea"
                  rows={5}
                  required
                />
              </Form.Group>
              <Button
                variant="primary"
                type="submit"
                onClick={handleSendRequest}
              >
                
                Envoyer
              </Button>
            </div>
          </Form>
        </Offcanvas.Body>
      </Offcanvas>
    </>
  );
}

export default StickyDetails;
