import Card from "react-bootstrap/Card";
import Button from "react-bootstrap/Button";
import Badge from "react-bootstrap/Badge";
import { Container } from "react-bootstrap";
import VehicleDetails from "../../screens/VehicleDetails";

import OverlayTrigger from "react-bootstrap/OverlayTrigger";
import Popover from "react-bootstrap/Popover";


export const VehicleCard = ({ vehicleID, brand, vehicleModel, productionYear, mileage, fuelType, transmission, vehicleCondition, price, photo, options }) => {

const popoverOptions = (
  <Popover id="popover-options">
    <Popover.Header
      as="h3"
      className="popover-title"
    >
      Options et équipements
    </Popover.Header>
    <Popover.Body className="popover-text">
        <ul>
          {options.map((option, index) => (
            <li key={index}>{option}</li>
          ))}
        </ul>
      </Popover.Body>
  </Popover>
)

const baseUrl = 'http://localhost:3000';

  return (
    <Card
      style={{ width: "18rem", borderRadius: "0px", height: "auto" }}
      className="bg-light m-1"
    >
      <Container className="bg-primary vehicle-card-tag"> </Container>
      <Card.Img
        className="mt25"
        variant="top"
        src={baseUrl + photo}
      />
      <Card.Body>
      <Card.Title className="text-primary vehicle-card-title">
          {brand} - {vehicleModel}
        </Card.Title>
        <Card.Text className="text-dark mb-4">
          {productionYear} - {mileage} km <br />
          {fuelType} - {transmission} <br />
          Etat : {vehicleCondition} <br />
        </Card.Text>
        <Container className="d-flex justify-content-center">
          <OverlayTrigger
            trigger="click"
            placement="right"
            overlay={popoverOptions} 
          >
            <Button variant="outline-dark">Voir les options...</Button>
          </OverlayTrigger>
        </Container>
        <Container className="text-center vehicle-card-price mt-4">
          <h3>
            {price}€
            <Badge
              bg="secondary"
              className="align-top bg-none text-primary p-0"
              style={{ fontSize: "0.5em" }}
            >
              TTC
            </Badge>
          </h3>
        </Container>
        <div className=" d-flex flex-row-reverse">
          <Button variant="primary"
          onClick={handleOpenDetails}>Détails</Button>
        </div>
      </Card.Body>
    </Card>
  );
};

export default VehicleCard;
