import React, { useState, useEffect } from "react";
import Table from "react-bootstrap/Table";
import Container from "react-bootstrap/Container";
import Button from "react-bootstrap/Button";
import { format, parseISO } from "date-fns";
import { fr } from "date-fns/locale";
import { scheduleGetAllDays } from "../../../serverRelated/ApiRequest";

const getCurrentYear = () => {
  const currentDate = new Date();
  return currentDate.getFullYear();
};

export const ScheduleManagement = () => {
  const [preparedData, setPreparedData] = useState([]);
  const [weekNumbers, setWeekNumbers] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      const year = getCurrentYear();
      try {
        const daysResponse = await scheduleGetAllDays(year);
        const { groupedData, weekNumbers: fetchedWeekNumbers } = prepareDataForDisplay(daysResponse);
        setPreparedData(groupedData);
        setWeekNumbers(fetchedWeekNumbers);
      } catch (error) {
        console.error("Erreur lors de la récupération des horaires:", error);
      }
    };
    fetchData();
  }, []);

  const prepareDataForDisplay = (data) => {
    const groupedData = [];
    const weekNumbers = [];
    Object.keys(data).forEach((weekNumber) => {
      const week = data[weekNumber];
      if (week.length > 0) {
        groupedData.push(week);
        weekNumbers.push(weekNumber);
      }
    });
    return { groupedData, weekNumbers };
  };

  const toggleDayState = (weekIndex, dayIndex) => {
    const newData = [...preparedData];
    const day = newData[weekIndex][dayIndex];
    day.isOpen = !day.isOpen;
    setPreparedData(newData);
  };

  const handleInputChange = (event, weekIndex, dayIndex, type) => {
    const newData = [...preparedData];
    const day = newData[weekIndex][dayIndex];
    const key = type.toLowerCase().replace(/\s/g, "");
    day[key] = event.target.value;
    setPreparedData(newData);
  };

  const copyDayContent = (dayId) => {
    console.log(`copyDayContent déclenché pour le jour ${dayId}`);
  };

  const pasteStoredDayContent = (dayId) => {
    console.log(`pasteStoredDayContent déclenché pour le jour ${dayId}`);
  };

  return (
    <>
      <Container>
        {preparedData.map((week, index) => (
          <div key={index}>
            <h3>Semaine {weekNumbers[index]}</h3>
            <Table striped bordered hover className="text-center mt-4" responsive>
              <thead>
                <tr>
                  {["Date", "État", "Ouverture Matin", "Fermeture Matin", "Ouverture Après-midi", "Fermeture Après-midi", "Actions"].map((header, headerIndex) => (
                    <th key={headerIndex}>{header}</th>
                  ))}
                </tr>
              </thead>
              <tbody>
                {week.map((day, dayIndex) => (
                  <tr key={dayIndex}>
                    <td>{format(parseISO(day.date), "EEEE dd MMMM yyyy", { locale: fr })}</td>
                    <td>
                      <Button
                        variant={day.isOpen ? "success" : "danger"}
                        onClick={() => toggleDayState(index, dayIndex)}
                      >
                        {day.isOpen ? "Ouvert" : "Fermé"}
                      </Button>
                    </td>
                    {["Ouverture Matin", "Fermeture Matin", "Ouverture Après-midi", "Fermeture Après-midi"].map((type, typeIndex) => (
                      <td key={typeIndex}>
                        <input
                          type="time"
                          value={day[type.toLowerCase().replace(/\s/g, "")] || ""}
                          onChange={(e) => handleInputChange(e, index, dayIndex, type)}
                          disabled={!day.isOpen}
                        />
                      </td>
                    ))}
                    <td>
                      <Button variant="primary" onClick={() => copyDayContent(day.id)}>
                        Copier
                      </Button>
                      {' '}
                      <Button variant="secondary" onClick={() => pasteStoredDayContent(day.id)}>
                        Coller
                      </Button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </Table>
          </div>
        ))}
      </Container>
    </>
  );
};

export default ScheduleManagement;
