import { Modal, Button, Form } from "react-bootstrap";
import CreatableSelect from "react-select/creatable";
import { useState, useEffect } from "react";
import fetchVehicles from "../../../serverRelated/fetchData/fetchVehicles";
import { capitalizeFirstLetter } from "../../../functions/capitalizeFirstLetter";

export const DeleteVehicleModal =  ({ show, onHide }) => {

  const [selectedVehicle, setSelectedVehicle] = useState(null);
  const [vehicle, setVehicles] = useState(null); 

  useEffect(() => {
    const handleFetchVehicles = async () => {
      try {
        const response = await fetchVehicles();
        const data = await response.json();

        const transformedvehicles = data.map((vehicle) => ({
          value: vehicle.id.toString(),
          label: vehicle.id + " - " + capitalizeFirstLetter(vehicle.VehicleModel.Brand.name) + " " + capitalizeFirstLetter(vehicle.VehicleModel.name) + " " + vehicle.VehicleModel.name,
        }));

        setVehicles(transformedvehicles); // Modifiez pour remplacer complètement les marques
      } catch (error) {
        console.error("Error fetching options:", error);
      }
    };
  }, []);

    return (
        <Modal show={show} onHide={onHide}>
        <Modal.Header closeButton>
          <Modal.Title>Titre du modal</Modal.Title>
        </Modal.Header>
        <Modal.Body>
        <Form.Label>
          <h3 className="key-info-title">Marque :</h3>
        </Form.Label>
        <CreatableSelect isClearable is isSearchable onChange={setSelectedVehicle}
          options={vehicle} />
    
        </Modal.Body>
        <Modal.Footer>
          <Button variant="primary" onClick={handleDeleteVehicle}> Supprimer </Button>
          <Button variant="secondary" onClick={onHide}> Annuler </Button>
        </Modal.Footer>
      </Modal>
    )

    }

    export default DeleteVehicleModal
