import React, { useState, useEffect } from "react";
import Table from "react-bootstrap/Table";
import Container from "react-bootstrap/Container";
import { scheduleGetAllDays } from "../../../serverRelated/ApiRequest";

import { format, parseISO } from "date-fns";
import { fr } from "date-fns/locale"; // Importer les locales françaises
import { getWeek } from 'date-fns';

import Button from "react-bootstrap/Button"; // Assurez-vous d'importer Button

const getCurrentYear = () => {
  const currentDate = new Date();
  return currentDate.getFullYear();
};

export const ScheduleManagement = () => {
  
  const [copiedDayData, setCopiedDayData] = useState(null);
  const [preparedData, setPreparedData] = useState([]);

  const toggleDayState = (weekIndex, dayIndex) => {
    // Copie profonde pour éviter la mutation directe de l'état
    const newData = [...preparedData];
    const day = newData[weekIndex][dayIndex];

    // Change l'état isOpen du jour concerné
    day.isOpen = !day.isOpen;

    // Si le jour est maintenant fermé, réinitialisez également les horaires
    if (!day.isOpen) {
      day.morningOpening = "";
      day.morningClosing = "";
      day.afternoonOpening = "";
      day.afternoonClosing = "";
    }

    // Met à jour l'état du composant avec les nouvelles données
    setPreparedData(newData);

    // Si nécessaire, mettez à jour les données sur le serveur ici
  };

  const copyDayContent = (dayId) => {
    const dayToCopy = preparedData.flat().find(day => day.id === dayId);
  
    if (dayToCopy) {
      // Exclut la propriété 'date' lors de la copie
      const { date, ...rest } = dayToCopy;
      setCopiedDayData({...rest});
      console.log(`Contenu du jour ${dayId} copié, à l'exception de la date.`);
    }
  };
  

  const pasteStoredDayContent = (dayId) => {
    if (!copiedDayData) {
      console.log("Aucun contenu à coller.");
      return;
    }
  
    const newData = preparedData.map(week => 
      week.map(day => 
        day.id === dayId ? {...day, ...copiedDayData, id: day.id} : day
      )
    );
  
    setPreparedData(newData);
    console.log(`Contenu collé dans le jour ${dayId}.`);
  };
  

  useEffect(() => {
    const fetchData = async () => {
      const year = getCurrentYear();
      try {
        // Assurez-vous que les fonctions renvoient directement un tableau ou extrayez le tableau comme nécessaire
        const daysResponse = await scheduleGetAllDays(year);
        console.log("daysResponse", daysResponse);

        const preparedData = prepareDataForDisplay(daysResponse);
        setPreparedData(preparedData);
      } catch (error) {
        console.error("Erreur lors de la récupération des horaires:", error);
      }
    };

    fetchData();
  }, []);

  const prepareDataForDisplay = (data) => {
    const groupedData = [];
    let currentGroup = [];

    Object.keys(data).forEach((weekNumber) => {
      const week = data[weekNumber];
      // Pour chaque semaine, vérifiez si vous avez plus de 7 jours et ajustez en conséquence.
      week.forEach((day, index) => {
        if (index < 7) {
          // Assurez-vous de ne prendre que les 7 premiers jours
          currentGroup.push(day);
        }
      });

      if (currentGroup.length > 0) {
        groupedData.push(currentGroup);
        currentGroup = [];
      }
    });

    return groupedData;
  };

  const handleInputChange = (event, weekIndex, dayIndex, type) => {
    const newValue = event.target.value !== "" ? event.target.value : null;

    const newData = preparedData.map((week) => week.map((day) => ({ ...day })));

    const key = type.toLowerCase().replace(/\s/g, ""); // Convertit "Ouverture Matin" en "ouverturematin", par exemple
    newData[weekIndex][dayIndex][key] = newValue;

    setPreparedData(newData);
  };

  const handleInputBlur = (event, weekIndex, dayIndex, header) => {
    const newValue = event.target.value;
    const key = header.toLowerCase().replace(/\s/g, ""); // Convertit l'en-tête en clé correspondante

    // Copie profonde pour éviter la mutation directe de l'état
    const newData = [...preparedData];
    const day = newData[weekIndex][dayIndex];

    // Met à jour la valeur spécifique
    day[key] = newValue || ""; // Utilisez une chaîne vide si la nouvelle valeur est falsy

    // Met à jour l'état du composant avec les nouvelles données
    setPreparedData(newData);

    // Ici, vous pourriez également envisager d'envoyer une mise à jour à votre serveur
    // pour refléter le changement d'état, par exemple :
    // updateDayOnServer(day.id, {[key]: newValue});
  };

  const openDaysInWeek = (weekIndex) => {
    const newData = [...preparedData];
  
    newData[weekIndex] = newData[weekIndex].map(day => ({
      ...day,
      isOpen: true, // Ouvre le jour
      // Vous pouvez laisser les horaires d'ouverture et de fermeture inchangés ou les définir selon un besoin spécifique
    }));
  
    setPreparedData(newData);
  
    // Si nécessaire, envoyez les mises à jour au serveur ici
  };
  
  const pasteStoredWeekContent = (weekId) => {
    console.log(`copyWeekContent 1 déclenché pour la semaine ${weekId}`);
  };
  const copyWeekContent = (weekId) => {
    console.log(`copyWeekContent 1 déclenché pour la semaine ${weekId}`);
  };

  const closeDaysInWeek = (weekIndex) => {
    // Copie profonde pour éviter la mutation directe de l'état
    const newData = [...preparedData];

    // Parcours tous les jours de la semaine spécifiée et les ferme
    newData[weekIndex] = newData[weekIndex].map((day) => ({
      ...day,
      isOpen: false, // Ferme le jour
      morningOpening: "", // Réinitialise les horaires d'ouverture et de fermeture
      morningClosing: "",
      afternoonOpening: "",
      afternoonClosing: "",
    }));

    // Met à jour l'état du composant avec les nouvelles données
    setPreparedData(newData);

    // Si nécessaire, mettez à jour les données sur le serveur ici
  };

  const rowsHeaders = [
    "Date",
    "État",
    "Ouverture Matin",
    "Fermeture Matin",
    "Ouverture Après-midi",
    "Fermeture Après-midi",
    `Actions`,
  ];

  return (
    <>
      <Container>
        {preparedData.map((week, index) => (
          <>
            <div>
              <h2
                key={`week-title-${index}`}
                className="text-light mt-5"
              >
                Semaine {index + 1}
              </h2>
              <Button
                variant="secondary"
                onClick={() => copyWeekContent(index)}
              >
                Copier
              </Button>{" "}
              <Button
                variant="secondary"
                onClick={() => copyWeekContent(index)}
              >
                Coller
              </Button>{" "}
              <Button
                variant="secondary"
                onClick={() => openDaysInWeek(index)}
              >
                Ouvrir la semaine
              </Button>
              <Button
                variant="secondary"
                onClick={() => closeDaysInWeek(index)}
              >
                Fermer la semaine
              </Button>
            </div>

            <Table
              key={index}
              striped
              bordered
              hover
              className="text-center mt-4"
              responsive
            >
              <tbody>
                {rowsHeaders.map((header, headerIndex) => (
                  <tr key={headerIndex}>
                    <th>{header}</th>
                    {week.map((day, dayIndex) => {
                      let cellContent;
                      switch (header) {
                        case "Date":
                          const date = parseISO(day.date); // Supposons que vous avez déjà importé parseISO et format
                          cellContent = format(date, "EEEE dd MMMM yyyy", {
                            locale: fr,
                          });
                          break;
                        case "État":
                          return (
                            <td key={`${headerIndex}-${dayIndex}`}>
                              <Button
                                onClick={() => toggleDayState(index, dayIndex)}
                                style={{
                                  backgroundColor: day.isOpen ? "green" : "red",
                                  color: "white",
                                }}
                              >
                                {day.isOpen ? "Ouvert" : "Fermé"}
                              </Button>
                            </td>
                          );
                        case "Ouverture Matin":
                        case "Fermeture Matin":
                        case "Ouverture Après-midi":
                        case "Fermeture Après-midi":
                          cellContent = (
                            <input
                              key={`input-${dayIndex}-${
                                day.isOpen ? "open" : "closed"
                              }`}
                              type="time"
                              value={
                                day[header.toLowerCase().replace(/\s/g, "")] ||
                                ""
                              }
                              onChange={(e) =>
                                handleInputChange(e, index, dayIndex, header)
                              }
                              onBlur={(e) =>
                                handleInputBlur(e, index, dayIndex, header)
                              }
                              disabled={!day.isOpen}
                              style={{
                                backgroundColor: day.isOpen
                                  ? "lightyellow"
                                  : "lightgrey",
                              }}
                            />
                          );
                          break;
                        case "Actions":
                          cellContent = (
                            <div>
                              <Button
                                variant="secondary"
                                onClick={() => pasteStoredDayContent(day.id)}
                              >
                                Coller
                              </Button> <Button
                                variant="secondary"
                                onClick={() => copyDayContent(day.id)}
                              >
                                Copier
                              </Button>
                            </div>
                          );
                          break;

                        default:
                          cellContent = "N/A";
                      }
                      return (
                        <td key={`${headerIndex}-${dayIndex}`}>
                          {cellContent}
                        </td>
                      );
                    })}
                  </tr>
                ))}
              </tbody>
            </Table>
          </>
        ))}
      </Container>
    </>
  );
};

export default ScheduleManagement;
