import Card from "react-bootstrap/Card";
import Button from "react-bootstrap/Button";
import Badge from "react-bootstrap/Badge";
import carImage from "../../img/toyota_aygoxxlimitedsu1b_angularfront.jpg";
import { Container } from "react-bootstrap";

import OverlayTrigger from "react-bootstrap/OverlayTrigger";
import Popover from "react-bootstrap/Popover";

const popoverOptions = (
  <Popover id="popover-options">
    <Popover.Header
      as="h3"
      className="popover-title"
    >
      Options et équipements
    </Popover.Header>
    <Popover.Body className="popover-text">
      {/* cette donnée est dans la table rel_vehicle_options avec l'id de ce véhicule et l'id de l'option qui est dans la table options, il peut y avoir plusieurs option utiliser une ul li */}
    </Popover.Body>
  </Popover>
)


export const VehicleCard = (vehicle) => {

const { marque, modele, annee, kilometrage, carburant, transmission, etat, price, photo, options } = vehicle;


  return (
    <Card
      style={{ width: "18rem", borderRadius: "0px", height: "auto" }}
      className="bg-light m-1"
    >
      <Container className="bg-primary vehicle-card-tag"> </Container>
      <Card.Img
        className="mt25"
        variant="top"
        src={photo}
      />
      <Card.Body>
      <Card.Title className="text-primary vehicle-card-title">
          {marque} - {modele}
        </Card.Title>
        <Card.Text className="text-dark mb-4">
          {annee} - {kilometrage} km <br />
          {carburant} - {transmission} <br />
          Etat : {etat} <br />
        </Card.Text>
        <Container className="d-flex justify-content-center">
          <OverlayTrigger
            trigger="click"
            placement="right"
            overlay={popoverOptions} 
          >
            <Button variant="outline-dark">Voir les options...</Button>
          </OverlayTrigger>
        </Container>
        <Container className="text-center vehicle-card-price mt-4">
          <h3>
            {price}€
            <Badge
              bg="secondary"
              className="align-top bg-none text-primary p-0"
              style={{ fontSize: "0.5em" }}
            >
              TTC
            </Badge>
          </h3>
        </Container>
        <div className=" d-flex flex-row-reverse">
          <Button variant="primary">Détails</Button>
        </div>
      </Card.Body>
    </Card>
  );
};

export default VehicleCard;
