import React, { useState, useEffect } from "react";
import { Modal, Button, Form } from "react-bootstrap";
import CreatableSelect from "react-select/creatable";
import { capitalizeFirstLetter } from "../../functions/capitalizeFirstLetter";

const AddOptionModal = ({ show, onHide }) => {
  const [selectedOption, setSelectedOption] = useState(null);
  const [description, setDescription] = useState(""); // État pour la description
  const [options, setOptions] = useState([]);

  const fetchOptions = async () => {
    try {
        const response = await fetch("http://localhost:5001/api/options");
        const data = await response.json();

        const transformedOptions = data.map(option => ({
            value: option.id.toString(),
            label: capitalizeFirstLetter(option.name),
        }));

        setOptions(transformedOptions); 
    } catch (error) {
        console.error("Error fetching options:", error);
    }
};

useEffect(() => {
  
    fetchOptions();
}, []);

  useEffect(() => {
    const fetchOptions = async () => {
      try {
        const response = await fetch("http://localhost:5001/api/options");
        const data = await response.json();
        const transformedOptions = data.map((option) => ({
          value: option.id.toString(),
          label: capitalizeFirstLetter(option.name),
          description: option.description, // Inclure la description ici
        }));
        setOptions(transformedOptions);
      } catch (error) {
        console.error("Error fetching options:", error);
      }
    };
    fetchOptions();
  }, []);

const handleSelectOption = (option) => {
    setSelectedOption(option);
    setDescription(option ? option.description : ""); // Mettre à jour la description ici
  };

const handleAddOption = async () => {
    if (!description.trim() || !selectedOption) {
      alert("Veuillez fournir une description pour l'option.");
      return;}

    try {
        const response = await fetch("http://localhost:5001/api/options", {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify({
                name: selectedOption.label,
            }),
        });
        const data = await response.json();
        console.log(data);
    } catch (error) {
        console.error("Error adding option:", error);
    }
}

const handleModifyOption = async () => {
    if (selectedOption && selectedOption.value) {
      const newOptionName = prompt("Entrez le nouveau nom de la marque:", selectedOption.label);
  
      if (newOptionName) {
        try {
          const response = await fetch(`http://localhost:5001/api/brands/${selectedOption.value}`, {
            method: 'PUT',
            headers: {
              'Content-Type': 'application/json',
            },
            body: JSON.stringify({ name: newOptionName }),
          });
  
          if (response.ok) {
            // Mise à jour de l'état des marques
            setOptions(prevOptions =>
              prevOptions.map(brand =>
                brand.value === selectedOption.value
                  ? { ...brand, label: capitalizeFirstLetter(newOptionName) }
                  : brand
              )
            );
            setSelectedOption(null); // Réinitialiser la marque sélectionnée
            alert("Marque modifiée avec succès");
            fetchOptions();
          } else {
            // Gérer les erreurs de réponse
            alert("Erreur lors de la modification de la marque");
          }
        } catch (error) {
          console.error("Erreur lors de l'envoi de la requête", error);
          alert("Erreur lors de la modification de la marque");
        }
      }
    } else {
      alert("Veuillez sélectionner une marque à modifier");
    }
  };

  // Reste du code pour handleAddOption...

  return (
    <Modal
      show={show}
      onHide={onHide}
    >
      <Modal.Header closeButton>
        <Modal.Title>Titre du modal</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <Form.Label>
          <h3 className="key-info-title">Option et équipements :</h3>
        </Form.Label>
        <CreatableSelect
          isClearable
          isSearchable
          onChange={handleSelectOption}
          options={options}
        />
        <Form.Label>Description</Form.Label>
        <Form.Control
          as="textarea"
          rows={4}
          value={description} // Utiliser la valeur de l'état de la description ici
          onChange={(e) => setDescription(e.target.value)} // Permettre la modification manuelle de la description
        />
      </Modal.Body>
      <Modal.Footer>
        <Modal.Footer>
          <Button
            variant="primary"
            //onClick={handleAddOption}
          >
            Ajouter
          </Button>
          <Button
            variant="orange"
           // onClick={handleModifyOption}
          >
            Modifier
          </Button>
          <Button
            variant="danger"
            //onClick={() => handleDeleteOption(selectedOption?.value)}
          >
            Supprimer
          </Button>
          <Button
            variant="secondary"
            onClick={onHide}
          >
            {" "}
            Fermer{" "}
          </Button>
        </Modal.Footer>
      </Modal.Footer>
    </Modal>
  );
};

export default AddOptionModal;
