import Table from "react-bootstrap/Table";
import Button from "react-bootstrap/Button";
import { useState, useEffect } from "react";
import {
  fetchAdmins,
  modifyAdmin,
  deleteAdmin,
  addNewAdmin,
} from "../../../serverRelated/ApiRequest";
import { Form } from "react-bootstrap";
import Container from "react-bootstrap/Container";

export const AdminManagement = () => {
  const [Admins, setAdmins] = useState([]);

  useEffect(() => {
    const getAdmins = async () => {
      const data = await fetchAdmins();
      setAdmins(data);
    };
    getAdmins();
  }, []);

  const handlePrepAddNewAdmin = () => {
    const existingEmptyAdmin = Admins.some(
      (Admin) => Admin.id === "new"
    );

    if (!existingEmptyAdmin) {
      setAdmins([
        { id: "new", name: "", description: "", min_price: "" },
        ...Admins,
      ]);
    }
  };

  const handleAddNewAdmin = async () => {
    // Demande à l'utilisateur de saisir un mot de passe
    const password = window.prompt('Entrez le mot de passe pour le nouvel administrateur:');
  
    // Vérifie si l'utilisateur a cliqué sur "Annuler" ou a laissé le champ vide
    if (password === null || password.trim() === '') {
      console.log('Saisie de mot de passe annulée.');
      return;
    }
  
    // Crée un nouvel administrateur avec le mot de passe saisi
    const newAdminData = {
      id: 'new',
        name: '',
      password: password,
    };
  
    try {
      const addedAdmin = await addNewAdmin(newAdminData);
      setAdmins(Admins.map(Admin => Admin.id === 'new' ? addedAdmin : Admin));
    } catch (error) {
      console.error('Erreur lors de l\'ajout de l\'administrateur:', error);
      // Gérer l'erreur ici
    }
  };
  
  const handleModifyAdmin = async (id) => {
    const AdminToModify = Admins.find((Admin) => Admin.id === id);
    if (AdminToModify) {
      const updatedAdmin = await modifyAdmin(id, AdminToModify);
      setAdmins(
        Admins.map((Admin) =>
          Admin.id === id ? updatedAdmin : Admin
        )
      );
    }
  };

  const handleDeleteAdmin = async (id) => {
    const userConfirmed = window.confirm("Êtes-vous sûr de vouloir supprimer ce Admin ?");
  
    if (userConfirmed) {
      await deleteAdmin(id);
      setAdmins(Admins.filter((Admin) => Admin.id !== id));
    } 
      };
  

  const handleChange = (id, field, value) => {
    setAdmins(Admins.map(Admin => {
      if (Admin.id === id || (id === 'new' && Admin.id === 'new')) {
        return { ...Admin, [field]: value };
      }
      return Admin;
    }));
  };

   const handleChangePassword = (id) => {
   }


  return (
    <>
    <Container>
      <div className="bg-dark align-content-center">
        <h1 className="text-light">AdminManagement</h1>
   
        <div className="bg-dark d-flex justify-content-center">

      <Button className='mb-5 mx-auto' onClick={handlePrepAddNewAdmin}>
        Ajouter un nouveau Admin
      </Button>
      </div>    
      </div>
      <Table
        striped
        bordered
        hover
      >
        <thead>
          <tr>
            <th>Nom</th>
            <th>Email</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {Admins.map((Admin, index) => (
            <tr key={Admin.id || index}>
              <td>
                <Form.Control
                  value={Admin.name}
                  type="text"
                  onChange={(e) =>
                    handleChange(Admin.id, "name", e.target.value)
                  }
                />
              </td>
              <td>
                <Form.Control
                  value={Admin.email}
                  type="text"
                  onChange={(e) =>
                    handleChange(Admin.id, "email", e.target.value)
                  }
                />
              </td>

              <td className="d-flex justify-content-around">
                {Admin.id && Admin.id !== "new" ? (
                  <>
                    <Button onClick={() => handleModifyAdmin(Admin.id)}>
                      Appliquer les modifications
                    </Button>
                    <Button onClick={() => handleChangePassword(Admin.id)}>
                        Modifier le mot de passe
                    </Button>
                    <Button
                      variant="danger"
                      onClick={() => handleDeleteAdmin(Admin.id)}
                    >
                      Supprimer
                    </Button>
                  </>
                ) : (
                  <Button
                    variant="success"
                    onClick={handleAddNewAdmin}
                  >
                    Créer nouveau Admin
                  </Button>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
      </Container>
    </>
  );
};

export default AdminManagement;
