import React, { useState, useEffect } from "react";
import { Button, Form } from "react-bootstrap";
import Select from "react-select";
import { capitalizeFirstLetter } from "../../../../functions/capitalizeFirstLetter";
import AddVehicleModelModal from "../../../addingEntries/AddVehicleModelModal";
// Renommez cette variable pour éviter les conflits
let initialModels = [
  // { value: "id", label: "Model name" },
];

export const ModelForm = ({onModelChange}, brand) => {
  const [showModal, setShowModal] = useState(false);
  const handleOpenModal = () => {
    setShowModal(true);
    setSelectedModel(null);
  };

  const handleChange = (selectedOption) => {
    setSelectedModel(selectedOption);
    onModelChange(selectedOption.value); // Appeler le callback avec la valeur sélectionnée
  };

  const handleCloseModal = () => setShowModal(false);

  const [selectedModel, setSelectedModel] = useState(null);
  const [models, setModels] = useState(initialModels); // Utilisez le nom renommé ici

  const onModalClose = () => {
    fetchModels(); // Ceci met à jour la liste des modèles
  };

  const fetchModels = async () => {
    try {
      const response = await fetch("http://localhost:5001/api/vehicleModels");
      const data = await response.json();

      const transformedModels = data.map((model) => ({
        value: model.id.toString(),
        label: capitalizeFirstLetter(model.name),
      }));

      setModels(transformedModels); // Modifiez pour remplacer complètement les marques
    } catch (error) {
      console.error("Error fetching options:", error);
    }
  };
  useEffect(() => {
    fetchModels();
  }, []);

  return (
    <>
      <Form.Label>
        <h3 className="key-info-title">Model :</h3>
      </Form.Label>
      <Select
      value={selectedModel}
      onChange={handleChange}
      options={models}
      isSearchable
    />
      <Button onClick={handleOpenModal}>Ajouter une marque</Button>
      <AddVehicleModelModal
        show={showModal}
        onHide={handleCloseModal}
        onModalClose={onModalClose}
      />
    </>
  );
};

export default ModelForm;
