import React, { useState, useEffect } from "react";
import { Button, Form } from "react-bootstrap"
import { fetchGarage, modifyGarage } from "../../serverRelated/ApiRequest";

export const GarageManagement = () => {
  const [garage, setGarage] = useState({});

  useEffect(() => {
    const getGarageData = async () => {
      try {
        const data = await fetchGarage();
        setGarage(data);
      } catch (error) {
        console.error("Error fetching garage data:", error);
      }
    };

    getGarageData();
  }, []);

  const handleModify = async () => {
    try {
      await modifyGarage(garage);
    
    } catch (error) {
      console.error("Error modifying garage:", error);
    }
  };

  return (
    <div className="bg-dark align-content-center">
      <h1 className="text-light">Informations du garage</h1>
      <div className="bg-dark d-flex justify-content-center">
        <Button
          className="mb-5 mx-auto"
          onClick={handleModify}
        >
          Appliquer les modifications
        </Button>
      </div>

      <div className="bg-dark d-flex justify-content-center">
        <Form.Label>
          <h3 className="text-light">Adresse</h3>
        </Form.Label>

        <Form.Control
          value={garage.adress}
          type="text"
        />
        <Form.Control
          value={garage.city}
          type="text"
        />

        <Form.Control
          value={garage.zip_code}
          type="text"
        />

        <Form.Label>
          <h3 className="text-light">Contact</h3>{" "}
        </Form.Label>

        <Form.Control
          value={garage.telephone}
          type="number"
        />
        <Form.Control
          value={garage.mail}
          type="text"
        />
      </div>
    </div>
  );
};
