import React, { useState, useEffect } from "react";
import { Accordion } from "react-bootstrap";
import Container from "react-bootstrap/Container";
import Form from "react-bootstrap/Form";
import OverlayTrigger from "react-bootstrap/OverlayTrigger";
import Tooltip from "react-bootstrap/Tooltip";
import { capitalizeFirstLetter } from "../../functions/capitalizeFirstLetter";
import { fetchBrands, fetchVehicleModels, fetchVehicleTypes } from "../../";

let desired_default_type = "voiture"; // Type de véhicule à cocher par défaut

const renderTooltip = (description) => (
  <Tooltip id="button-tooltip">{description}</Tooltip>
);

const VehicleFilters = () => {
  const [filters, setFilters] = useState({
    selectedVehicleTypes: [desired_default_type],
    brands: [],
    vehicleModels: [],
    uniqueVehicleTypes: [],
    vehicleSubtypes: []
  });

  useEffect(() => {
    const initFilters = async () => {
      try {
        const brandsData = await fetchBrands();
        const vehicleModelsData = await fetchVehicleModels();
        const vehicleTypesData = await fetchVehicleTypes();
        setFilters(prevFilters => ({
          ...prevFilters,
          brands: brandsData,
          vehicleModels: vehicleModelsData,
          uniqueVehicleTypes: [...new Set(vehicleTypesData.map(type => type.vehicle_type_name))],
          vehicleSubtypes: vehicleTypesData
        }));
      } catch (error) {
        console.error("Erreur lors de l'initialisation des filtres:", error);
      }
    };
    initFilters();
  }, []);

  const handleVehicleTypeChange = (event) => {
    const typeValue = event.target.value;
    setFilters(prevFilters => ({
      ...prevFilters,
      selectedVehicleTypes: event.target.checked
        ? [...prevFilters.selectedVehicleTypes, typeValue]
        : prevFilters.selectedVehicleTypes.filter(type => type !== typeValue),
      vehicleSubtypes: prevFilters.vehicleSubtypes.filter(subtype =>
        event.target.checked
          ? subtype.vehicle_type_name === typeValue
          : subtype.vehicle_type_name !== typeValue
      )
    }));
  };
  
  return (
    <Container
      className="bg-dark filters-wrapper"
      style={{ width: "20%" }}
    >
      <Accordion
        defaultActiveKey={["0"]}
        style={{
          "--bs-accordion-bg": "transparent",
          "--bs-accordion-border-color": "transparent",
        }}
        alwaysOpen
      >
        <Accordion.Item eventKey="0">
          <Accordion.Header className="filter-accordion-title">
            Filtre
          </Accordion.Header>
          <Accordion.Body>
            {/* Types */}
            <Accordion
              defaultActiveKey={["0"]}
              style={{
                "--bs-accordion-bg": "transparent",
                "--bs-accordion-border-color": "transparent",
              }}
              alwaysOpen
            >
              <Accordion.Item eventKey="0">
                <Accordion.Header className="filter-accordion-sub-title">
                  Type de véhicule
                </Accordion.Header>
                <Accordion.Body>
                  <Form.Control
                    className="mb-3 filter-search-form  m-auto"
                    type="text"
                    placeholder="Search"
                  />
                  <Form className="px-3 filter-options-wrapper">
                    {uniqueVehicleTypes.map((type, index) => (
                      <Form.Check
                        key={index}
                        label={`${capitalizeFirstLetter(
                          type
                        )} (nb de véhicules)`}
                        value={type}
                        onChange={handleVehicleTypeChange}
                        className="text-light"
                        checked={selectedVehicleTypes.includes(type)}
                      />
                    ))}
                  </Form>
                </Accordion.Body>
              </Accordion.Item>
            </Accordion>
            {/* SousTypes */}
            <Accordion
              defaultActiveKey={["0"]}
              style={{
                "--bs-accordion-bg": "transparent",
                "--bs-accordion-border-color": "transparent",
              }}
              alwaysOpen
            >
              <Accordion.Item eventKey="0">
                <Accordion.Header className="filter-accordion-sub-title">
                  Sous-type
                </Accordion.Header>
                <Accordion.Body>
                  <Form.Control
                    className="mb-3 filter-search-form  m-auto"
                    type="text"
                    placeholder="Search"
                  />
                  
                  <Form className="px-3 filter-options-wrapper">
                    {vehicleSubtypes.map((subtype, index) => (

                        <OverlayTrigger
                        key={index}
                        placement="right"
                        delay={{ show: 250, hide: 400 }}
                        overlay={renderTooltip(subtype.description)} // Utilisez la description du modèle ici
                        >
                        <div>
                        <Form.Check
                        key={index}
                        label={`${capitalizeFirstLetter(
                          subtype.vehicle_subtype_name
                        )} (nb de véhicules)`}
                        className="text-light"
                      />
                      </div>
                        </OverlayTrigger>
                    ))}
                  </Form>
                </Accordion.Body>
              </Accordion.Item>
            </Accordion>

            {/* *Brand */}
            <Accordion
              defaultActiveKey={["0"]}
              style={{
                "--bs-accordion-bg": "transparent",
                "--bs-accordion-border-color": "transparent",
              }}
              alwaysOpen
            >
              <Accordion.Item eventKey="0">
                <Accordion.Header className="filter-accordion-sub-title">
                  Marques
                </Accordion.Header>
                <Accordion.Body>
                  <Form.Control
                    className="mb-3 filter-search-form  m-auto"
                    type="text"
                    placeholder="Search"
                  />
                  <Form className="px-3 filter-options-wrapper">
                    {brands.map((brand, index) => (
                      <Form.Check
                        key={index}
                        label={`${capitalizeFirstLetter(
                          brand.name
                        )} (nb de véhicules)`}
                        className="text-light"
                      />
                    ))}
                  </Form>
                </Accordion.Body>
              </Accordion.Item>
            </Accordion>

            {/* Models */}
            <Accordion
              defaultActiveKey={["0"]}
              style={{
                "--bs-accordion-bg": "transparent",
                "--bs-accordion-border-color": "transparent",
              }}
              alwaysOpen
            >
              <Accordion.Item eventKey="0">
                <Accordion.Header className="filter-accordion-sub-title">
                  Modèles
                </Accordion.Header>
                <Accordion.Body>
                  <Form.Control
                    className="mb-3 filter-search-form  m-auto"
                    type="text"
                    placeholder="Search"
                  />
                  <Form className="px-3 filter-options-wrapper">
                    {vehicleModels.map((vehicleModels, index) => (
                      <OverlayTrigger
                        key={index}
                        placement="right"
                        delay={{ show: 250, hide: 400 }}
                        overlay={renderTooltip(vehicleModels.description)} // Utilisez la description du modèle ici
                      >
                        <div>
                          <Form.Check
                            key={index}
                            label={`${vehicleModels.name} (nb)`}
                            className="text-light"
                          />
                        </div>
                      </OverlayTrigger>
                    ))}
                  </Form>
                </Accordion.Body>
              </Accordion.Item>
            </Accordion>
          </Accordion.Body>
        </Accordion.Item>
      </Accordion>
    </Container>
  );
};

export default VehicleFilters;
