import React, { useState, useEffect } from "react";
import { Button, Form } from "react-bootstrap";
import Select from "react-select";
import { capitalizeFirstLetter } from "../../../../functions/capitalizeFirstLetter";
import AddVehicleModelModal from "../../../addingEntries/AddVehicleModelModal";

export const VehicleTypeForm = ({onTypeChange}) => {
  const [showModal, setShowModal] = useState(false);

  const [selectedVehicleType, setSelectedVehicleType] = useState(null);
  const [vehicleTypes, setVehicleTypes] = useState([]);

  useEffect(() => {
    const fetchVehicleTypes = async () => {
      try {
        const response = await fetch("http://localhost:5001/api/vehicleTypes");
        const data = await response.json();

        let seenTypes = {};
        const transformedVehicleTypes = data.reduce((unique, vehicleType) => {
          const typeName = vehicleType.name.toLowerCase();

          if (!seenTypes[typeName]) {
            seenTypes[typeName] = true;
            unique.push({
              value: vehicleType.id.toString(),
              label: capitalizeFirstLetter(vehicleType.name),
            });
          }
          return unique;
        }, []);

        setVehicleTypes(transformedVehicleTypes);
      } catch (error) {
        console.error("Error fetching vehicle types:", error);
      }
    };

    fetchVehicleTypes();
  }, []); // Ajoutez un tableau de dépendances vide

  const handleTypeChange = (selectedOption) => {
    setSelectedVehicleType(selectedOption);
    if (onTypeChange) {
      onTypeChange(selectedOption.value); // Transmettre la valeur sélectionnée au parent
    }
  };
  
  return (
    <>
      <Select
        value={selectedVehicleType}
        onChange={handleTypeChange}
        options={vehicleTypes}
        isSearchable
      />

      <Button
        variant="primary"
        onClick={() => handleOpenModal()}
      >
        Ajouter un type de véhicule
      </Button>
      <AddVehicleModelModal
        show={showModal}
        onHide={handleCloseModal}/>
    </>
  );
};

export default VehicleTypeForm;
