import React, { useState } from 'react';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';

function AdminConnexionPannel() {
  const [email, setEmail] = useState(''); // État pour l'email
  const [password, setPassword] = useState(''); // État pour le mot de passe

  // Gérer la soumission du formulaire
  const handleSubmit = async (event) => {
    event.preventDefault(); // Empêche le rechargement de la page
    try {
      const response = await fetch('http://localhost:5001/login', { // Remplacez par l'URL de votre serveur
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email, password }),
      });

      if (response.ok) {
        const data = await response.json();
        console.log('Connexion réussie:', data);
        // Gérer la connexion réussie ici (e.g., redirection, stockage du token, etc.)
      } else {
        console.error('Échec de la connexion');
        // Gérer l'échec de la connexion ici (e.g., afficher un message d'erreur)
      }
    } catch (error) {
      console.error('Erreur lors de la requête:', error);
      // Gérer les erreurs de requête ici
    }
  };

  return (
    <Form onSubmit={handleSubmit}> {/* Ajout de l'événement onSubmit ici */}
      <Form.Group className="mb-3" controlId="formBasicEmail">
        <Form.Label>Email address</Form.Label>
        <Form.Control
          type="email"
          placeholder="Enter email"
          value={email}
          onChange={(e) => setEmail(e.target.value)} // Mettre à jour l'état de l'email
        />
        <Form.Text className="text-muted">
          We'll never share your email with anyone else.
        </Form.Text>
      </Form.Group>

      <Form.Group className="mb-3" controlId="formBasicPassword">
        <Form.Label>Password</Form.Label>
        <Form.Control
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)} // Mettre à jour l'état du mot de passe
        />
      </Form.Group>
      <Button variant="primary" type="submit"> {/* Le type doit être "submit" pour soumettre le formulaire */}
        Submit
      </Button>
    </Form>
  );
}

export default AdminConnexionPannel;
