import React, { useEffect, useState } from 'react';
import { scheduleGetWeek } from "../../serverRelated/ApiRequest";

const getCurrentDate = () => {
  const now = new Date();
  // Cette fonction doit idéalement retourner le numéro de la semaine actuelle et l'année.
  return {
    year: now.getFullYear(),
    // La logique pour obtenir le numéro de la semaine doit être ici. Utilisez un placeholder pour l'exemple.
    weekStart: now.getDate() - now.getDay() + (now.getDay() === 0 ? -6 : 1), // Calcul simplifié pour l'exemple

  };
};

export const FooterSchedule = () => {
  const [schedule, setSchedule] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      const currentDate = getCurrentDate();
      try {
        const year = currentDate.year;
        const weekNumber = currentDate.weekNumber; // Utilisez le numéro de semaine pour récupérer les données

        // Assumez que vous ajustez votre API ou votre logique frontale pour utiliser un numéro de semaine
        const response = await scheduleGetWeek(year, weekNumber, 1);
        console.log("fetched schedule get week", response);

        // Ici, nous supposons que vous voulez afficher les jours de la semaine '11'
        // Vous devez ajuster cette logique selon vos besoins réels
        const weekData = response['11'];
        if (weekData) {
          setSchedule(weekData);
        } else {
          // Gérer le cas où les données de la semaine spécifique ne sont pas trouvées
          setSchedule([]);
        }
      } catch (error) {
        console.error("Erreur lors de la récupération des horaires:", error);
      }
    };

    fetchData();
  }, []);

  return (
    <div>
      {schedule.length > 0 ? (
        <ul>
          {schedule.map((day, index) => (
            <li key={index}>Date: {day.date}, Open: {day.isOpen.toString()}</li>
          ))}
        </ul>
      ) : (
        <p>Aucun horaire disponible</p>
      )}
    </div>
  );
};

export default FooterSchedule;
