import React, { useState, useEffect } from "react";
import { Container } from "react-bootstrap";
import GUIIcons from "../../general/GUIIcons";
import Offcanvas from "react-bootstrap/Offcanvas";
import { capitalizeFirstLetter } from "../../../functions/capitalizeFirstLetter";
import Button from "react-bootstrap/Button";
import { Tabs, Tab } from "react-bootstrap";
import Form from "react-bootstrap/Form";
import InputGroup from "react-bootstrap/InputGroup";

function StickyDetails({
  vehicleId,
  brand,
  vehicleModel,
  productionYear,
  mileage,
  fuelType,
  transmission,
  vehicleCondition,
  price,
  vehicleComment,
  vehicleModelDescription,
}) {
  const [showOffcanvasRequest, setShowOffcanvasRequest] = useState(false);

  const handleShowRequest = () => setShowOffcanvasRequest(true);
  const handleCloseRequest = () => setShowOffcanvasRequest(false);

  const [topValue, setTopValue] = useState(0);

  const handleScroll = () => {
    setTopValue(window.scrollY);
  };

  const [showOffcanvas, setShowOffcanvas] = useState(false);

  useEffect(() => {
    window.addEventListener("scroll", handleScroll);

    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  const handleSendRequest = async () => {

    if (isValidPhoneNumber(phoneNumber)) {
      if(isValidEmail(email)) {
        
        const newRequest = {
          requester_name: name, // Utilisez le nom saisi par l'utilisateur
          vehicle_id: vehicleId,
          message: message,
          phone: phoneNumber,
          email: email,
          prefer_phone: preferPhone,
          is_processed: false,
          received_at: new Date(),
          processed_at: null,
          gender: gender,
        };
  
      
    try {
      const response = await fetch("http://localhost:5001/api/requests", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(newRequest),
      });
      if (response.ok) {
        alert("Votre demande a bien été envoyée")
        handleCloseRequest();
      } else {
        alert("Une erreur est survenue");
      }
    } catch (error) {
      console.error("Error sending request:", error);
    }}
    else {
      alert("Veuillez entrer un email valide");
    }
  }
  else {
    alert("Veuillez entrer un numéro de téléphone valide");
  }
  };
  
  const [phoneNumber, setPhoneNumber] = useState("+336");
  const handlePhoneChange = (event) => {
    let input = event.target.value;
    if (input.startsWith("+336")) {
      input = "+336" + input.slice(4).replace(/[^0-9]/g, "");
    } else {
      input = "+336";
    }

    if (input.length > 13) {
      input = input.slice(0, 13);
    }
    setPhoneNumber(input);
  };

  

  //OffCanvas
  const [show, setShow] = useState(false);

  const [vehicleConditions, setVehicleConditions] = useState([]);
  useEffect(() => {
    const fetchVehicleConditions = async () => {
      try {
        const response = await fetch(
          "http://localhost:5001/api/vehicleConditions"
        );
        const data = await response.json();
        setVehicleConditions(data);
      } catch (error) {
        console.error("Error fetching vehicle conditions:", error);
      }
    };
    fetchVehicleConditions();
  }, []);

  const isValidPhoneNumber = (phoneNumber) => {
    if (!phoneNumber) {
      return false;
    }
    const validPrefixes = ["+336", "+337", "336", "337", "07", "06"];
    const startsWithValidPrefix = validPrefixes.some((prefix) =>
      phoneNumber.startsWith(prefix)
    );

    return startsWithValidPrefix;
  };

  const isValidEmail = (email) => {
    // Expression régulière pour vérifier le format de l'adresse e-mail
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;

    // Vérifier si l'adresse e-mail correspond au format regex
    return emailRegex.test(email);
  };

  const isValidMessage = (message) => {
    return message.length > 0;
  }

  return (
    <>
      <div
        style={{
          position: "absolute",
          top: `${topValue}px`,
          left: "1000px",
          width: "500px",
          height: "auto",
          transition: "all 0.5s ease",
        }}
        className="stick-info-wrapper"
      >
        <Container className="mt-2 mb-3">
          <h3 className="stick-info-title">
            {brand} - {vehicleModel}
          </h3>
          <p className="stick-infos">
            {productionYear} - {mileage} km
          </p>
          <p className="stick-infos">
            {fuelType} - {transmission}{" "}
          </p>
          <p
            className="stick-infos align-items-top"
            onClick={() => setShow(true)}
          >
            {" "}
            {vehicleCondition} <GUIIcons.Info className="info-icon" />
          </p>
        </Container>
        <div className="mb-5">
          <Tabs
            defaultActiveKey="condition-description"
            id="sticky-descriptions"
            className="bg-dark mt-4"
            fill
          >
            <Tab
              eventKey="condition-description"
              title="État du véhicule"
              className="bg-light pb-3 pt-3 px-3"
            >
              {vehicleComment}
            </Tab>
            <Tab
              eventKey="model-description"
              title="Commentaire du Modèle"
              className="bg-light pb-3 pt-3 px-3"
            >
              {vehicleModelDescription}
            </Tab>
          </Tabs>
        </div>
        <div className="d-flex flex-column justify-content-center align-items-center">
          <h4 className="stick-info-garantie">Garantie 12 mois</h4>
          <h3 className="stick-info-price">{price} €</h3>
          <h4 className="stick-info-garantie mt-4">
            Une question ? Un rendez-vous ?
          </h4>
          <Button
            className="btn btn-primary btn-lg mt-3"
            onClick={handleShowRequest}
          >
            Nous contacter
          </Button>
        </div>
      </div>

      <Offcanvas
        show={show}
        onHide={() => setShow(false)}
        className="bg-dark custom-offcanvas"
      >
        <Offcanvas.Header
          closeVariant="white"
          closeButton
        >
          <Offcanvas.Title className="mx-auto offcanvas-title">
            États des véhicules
          </Offcanvas.Title>
        </Offcanvas.Header>
        <Offcanvas.Body>
          <ul className="px-1">
            {vehicleConditions.map((condition, index) => (
              <div className="mb-3">
                <li
                  key={index}
                  className="text-light li-no-style"
                >
                  <span className="offcanvas-condition-name">
                    {" "}
                    {capitalizeFirstLetter(condition.name) + " : "}{" "}
                  </span>{" "}
                  <br />
                  <span className="offcanvas-condition-description">
                    {condition.description}
                  </span>
                </li>
                <hr />
              </div>
            ))}
          </ul>
        </Offcanvas.Body>
      </Offcanvas>

      <Offcanvas
        show={showOffcanvasRequest}
        onHide={handleCloseRequest}
        scroll={true}
        backdrop={false}
        className="bg-dark custom-offcanvas"
      >
        <Offcanvas.Header closeButton>
          <Offcanvas.Title>Contacter nous !</Offcanvas.Title>
        </Offcanvas.Header>
        <Offcanvas.Body>
          <Form
            className="text-light px-1 py-3"
          >
            <Form.Group className="mb-3">
              <Form.Check
                inline
                label="M."
                name="gender"
                type="radio"
                id={`inline-radio-mr`}
              />
              <Form.Check
                inline
                label="Mme."
                name="gender"
                type="radio"
                id={`inline-radio-md`}
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Nom</Form.Label>
              <Form.Control
                name="name"
                type="text"
                placeholder="Nom"
                required
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Adresse Email</Form.Label>
              <Form.Control
                name="email"
                type="email"
                placeholder="adresse@mail.fr"
                required
                isValid={!isValidEmail}
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Numéro de téléphone</Form.Label>
              <Form.Control
                name="phone"
                type="tel"
                defaultValue={phoneNumber}
                onChange={handlePhoneChange}
                required
                isInvalid={!isValidPhoneNumber}
              />
              <Form.Control.Feedback type="invalid">
                Entrez un numéro de téléphone valide
              </Form.Control.Feedback>
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Check
                label="Préférer être contacté par email"
                name="preference"
                type="checkbox"
                id={`inline-checkbox-md`}
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Message</Form.Label>
              <Form.Control
                as="textarea"
                rows={5}
                required
                isInvalid={!isValidField}
                placeholder="Votre message ici"
              />
            </Form.Group>

            <div className="text-center">
              <Button
                variant="primary"
                type="submit"
              >
                Envoyer
              </Button>
              <Button
                variant="secondary"
                className="ms-3"
                onClick={handleCloseRequest}
              >
                Annuler
              </Button>
            </div>
          </Form>
        </Offcanvas.Body>
      </Offcanvas>
    </>
  );
}

export default StickyDetails;
