import React, { useState, useEffect } from 'react';
import { Container } from 'react-bootstrap';
import GUIIcons from '../../general/GUIIcons';

function StickyDetails() {
  const [topValue, setTopValue] = useState(0);

  const handleScroll = () => {
    // Mettre à jour topValue en fonction de la position de défilement
    // Par exemple, vous pouvez simplement utiliser window.scrollY ou une formule personnalisée
    setTopValue(window.scrollY);
  };

  useEffect(() => {
    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <div style={{
        position: 'absolute',
        top: `${topValue}px`,
        left: '1000px',
        width: '500px',
        height: 'auto',
        transition: 'all 0.5s ease'
      }}
        className='stick-info-wrapper'>
        <Container className='mt-2 mb-3'>
        <h3 className="stick-info-title">Marque + Models </h3>
        <p className='stick-infos'> 2014 - 354 541 km </p>
        <p className='stick-infos'> diesel - automatique </p>
        <p className='stick-infos'> Etat : Bon <GUIIcons.Info/> </p>

        </Container>
        <Container className='mt-2 mb-3 stick-description-wrapper py-3'>
            <h4 className="stick-info-title text-dark">Description de l'expert</h4>
            <p className='stick-infos text-dark'>Waouw ce véhicule est en parfait état ça alors et il fait vroumvroum</p>
        </Container>
          <div className="d-flex flex-column justify-content-center align-items-center">

          <h4 className='stick-info-garantie'>Garantie 12 mois</h4>
          <h3 className="stick-info-price">1 520 €</h3>
          <h4 className='stick-info-garantie mt-4'>Une question ? Un rendez-vous ?</h4>
          <button className="btn btn-primary mb-3">Nous contacter</button>
          </div>
    </div>
  );
}

export default StickyDetails;
