import React, { useState } from 'react';
import { Accordion, Card, useAccordionToggle } from 'react-bootstrap';

const VehicleFilters = () => {
  const [openKeys, setOpenKeys] = useState([]);

  const toggleAccordionItem = (key) => {
    setOpenKeys(prevOpenKeys =>
      prevOpenKeys.includes(key)
        ? prevOpenKeys.filter(openKey => openKey !== key)
        : [...prevOpenKeys, key]
    );
  };

  return (
    <Accordion>
      {[...Array(3)].map((_, index) => {
        const eventKey = index.toString();
        const isCurrentlyOpen = openKeys.includes(eventKey);
        const decoratedOnClick = useAccordionToggle(eventKey, () => toggleAccordionItem(eventKey));
        
        return (
          <Card key={eventKey}>
            <Card.Header onClick={decoratedOnClick}>
              Filter {index + 1}
            </Card.Header>
            <Accordion.Collapse eventKey={eventKey} isCurrentlyOpen={isCurrentlyOpen}>
              <Card.Body>
                Content for filter {index + 1}
              </Card.Body>
            </Accordion.Collapse>
          </Card>
        );
      })}
    </Accordion>
  );
};

export default VehicleFilters;
