import React, { useState, useEffect } from "react";
import { Modal, Button, Row } from "react-bootstrap";
import CreatableSelect from "react-select/creatable";
import { capitalizeFirstLetter } from "../../functions/capitalizeFirstLetter";

const AddTransmissionModal = ({ show, onHide, onModalClose }) => {
  const [selectedTransmission, setSelectedTransmission] = useState(null);
  const [transmissions, setTransmissions] = useState([]);

  const handleCloseModal = () => {
    onHide();   
    onModalClose(); 
  };


  const fetchTransmissions = async () => {
    try {
      const response = await fetch("http://localhost:5001/api/transmissionTypes");
      const data = await response.json();
      const transformedTransmissions = data.map(T => ({
        value: T.id.toString(),
        label: capitalizeFirstLetter(T.name),
      }));
      setTransmissions(transformedTransmissions);
    } catch (error) {
      console.error("Error fetching transmissions:", error);
    }
  };

  useEffect(() => {
    fetchTransmissions();
  }, []);

  const handleSelectTransmission = (selectedTransmission) => {
    setSelectedTransmission(selectedTransmission);
  };

  const handleAddTransmission = async () => {
    if (selectedTransmission && selectedTransmission.__isNew__) {
      try {
        const newTransmission = {
          name: selectedTransmission.label,
        };
        const response = await fetch("http://localhost:5001/api/transmissionTypes", {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify(newTransmission),
        });

        if (response.ok) {
          fetchTransmissions();
        }
      } catch (error) {
        console.error("Error adding T:", error);
      }
    }
  };

  const handleModifyTransmission = async () => {
    if (selectedTransmission && !selectedTransmission.__isNew__) {
      const newName = prompt("Entrez le nouveau nom du type de carburant :", selectedTransmission.label);
      if (newName && newName.trim() !== "") {
        try {
          const updatedTransmission = {
            name: newName,
          };
          const response = await fetch(`http://localhost:5001/api/transmissionTypes/${selectedTransmission.value}`, {
            method: "PUT",
            headers: {
              "Content-Type": "application/json",
            },
            body: JSON.stringify(updatedTransmission),
          });

          if (response.ok) {
            fetchTransmissions();
          }
        } catch (error) {
          console.error("Error updating T:", error);
        }
      }
    }
  };

  const handleDeleteTransmission = async (transmissionId) => {
   
  };

  return (
    <Modal
      show={show}
      onHide={onHide}
    >
      <Modal.Header closeButton>
        <Modal.Title>Titre du modal</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <Row>
          <CreatableSelect
            isClearable
            isSearchable
            onChange={handleSelectTransmission}
            options={transmissions}
            value={selectedTransmission}
          />
        </Row>
      </Modal.Body>
      <Modal.Footer>
        <Button
          variant="primary"
          onClick={handleAddTransmission}
        >
          Ajouter
        </Button>
        <Button
          variant="orange"
          onClick={handleModifyTransmission}
        >
          Modifier
        </Button>
        <Button
          variant="danger"
          onClick={() => handleDeleteTransmission(selectedTransmission?.value)}
        >
          Supprimer
        </Button>
        <Button
          variant="secondary"
          onClick={handleCloseModal}
        >
          Fermer
        </Button>
      </Modal.Footer>
    </Modal>
  );
};

export default AddTransmissionModal;
