    return (
    
      <Card style={{ width: '18rem', border: 'none', background }} className="bg-light m-1">
      <Card.Body>
        <Card.Title  className="text-primary vehicle-card-title" >{service.name}</Card.Title>
        <Card.Text  className="text-dark mb-4">
        {service.description}
        </Card.Text>
          <h3 className="text-center vehicle-card-price mt-4" style={{ fontSize: "1.3em"}}>
            <p>{service.min_price}â‚¬ 
            <Badge
              bg="secondary"
              className="align-top bg-none text-primary p-0"
              style={{ fontSize: "0.5em" }}
            >
              TTC
            </Badge>
            Min</p>
        
          </h3>
      </Card.Body>
    </Card>
    );
  }
  export default ServiceCard;
