import React, { useState, useEffect } from "react";
import { Button, Form } from "react-bootstrap";
import Select from "react-select";
import { capitalizeFirstLetter } from "../../../../functions/capitalizeFirstLetter";
import AddTransmissionModal from "../../../addingEntries/AddTransmissionModal";
// Renommez cette variable pour éviter les conflits
let initialTransmissions = [
  // { value: "id", label: "Transmission name" },
];

export const TransmissionForm = (onTransmissionChange) => {
  const [showModal, setShowModal] = useState(false);
  const handleOpenModal = () => {
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setShowModal(false);
    setSelectedTransmission(null);
  };

  const [selectedTransmission, setSelectedTransmission] = useState(null);
  const [transmissions, setTransmissions] = useState(initialTransmissions); // Utilisez le nom renommé ici

  const fetchTransmissions = async () => {
    try {
      const response = await fetch("http://localhost:5001/api/transmissions");
      const data = await response.json();

      const transformedTransmissions = data.map((transmission) => ({
        value: transmission.id.toString(),
        label: capitalizeFirstLetter(transmission.name),
      }));

      setTransmissions(transformedTransmissions); // Modifiez pour remplacer complètement les marques
    } catch (error) {
      console.error("Error fetching options:", error);
    }
  };

  useEffect(() => {
    fetchTransmissions();
  }, []);

  const handleTransmissionChange = (selectedOption) => {

      setSelectedTransmission(selectedOption);
      if (onTransmissionChange) {
        onTransmissionChange(selectedOption.value); // Passer l'ID de la transmission au parent
      }
    };


  const handleModalClose = () => {
    fetchTransmissions(); 
  };

  return (
    <>
      <Form.Label>
        <h3 className="key-info-title">Types :</h3>
      </Form.Label>
      <Select
        value={selectedTransmission}
        onChange={handleTransmissionChange}
        options={transmissions}
        isSearchable
      />
      <Button onClick={handleOpenModal}>Ajouter une marque</Button>
      <AddTransmissionModal
  show={showModal}
  onHide={handleCloseModal}
  onModalClose={handleModalClose} // Maintenant, cette fonction est définie
/>


    </>
  );
};

export default TransmissionForm;
