import React, { useState, useEffect } from "react";
import Table from "react-bootstrap/Table";
import Container from "react-bootstrap/Container";
import { scheduleGetAllDays, scheduleGetDefaultWeek } from "../../../serverRelated/ApiRequest";

import { format, parseISO } from "date-fns";
import { af, fr } from "date-fns/locale"; // Importer les locales françaises
import { getWeek } from "date-fns";

import Button from "react-bootstrap/Button"; // Assurez-vous d'importer Button

const getCurrentYear = () => {
  const currentDate = new Date();
  return currentDate.getFullYear();
};

const minMaxValues = {
  morningOpeningMax: "13:00",
  morningClosingMin: "05:00",
  afternoonOpeningMax: "20:00",
  afternoonClosingMin: "13:00",
};



export const ScheduleManagement = () => {
  const [copiedDayData, setCopiedDayData] = useState(null);
  const [preparedData, setPreparedData] = useState([]);
  const [copiedWeekData, setCopiedWeekData] = useState(null);

  const toggleDayState = (weekIndex, dayIndex) => {
    const newData = [...preparedData];
    // Trouver le bon jour dans la propriété `days`
    const day = newData[weekIndex].days[dayIndex];

    // Modifier l'état isOpen
    day.isOpen = !day.isOpen;

    // Appliquer d'autres logiques si nécessaire...

    setPreparedData(newData);
  };

  const copyDayContent = (dayId) => {
    // Créer un nouveau tableau composé de tous les jours de toutes les semaines
    const allDays = preparedData.reduce(
      (acc, week) => acc.concat(week.days),
      []
    );

    // Trouver le jour spécifique par son ID dans le nouveau tableau aplati
    const dayToCopy = allDays.find((day) => day.id === dayId);

    if (dayToCopy) {
      // Exclure la propriété 'date' lors de la copie
      const { date, ...rest } = dayToCopy;
      setCopiedDayData({ ...rest });
      console.log(`Contenu du jour ${dayId} copié, à l'exception de la date.`);
    } else {
      console.log("Jour non trouvé.");
    }
  };

  const pasteStoredDayContent = (dayId) => {
    if (!copiedDayData) {
      console.log("Aucun contenu à coller.");
      return;
    }

    // Crée une nouvelle structure de données en parcourant chaque semaine
    const newData = preparedData.map((week) => {
      // Pour chaque semaine, parcourt ses jours pour trouver et mettre à jour le jour ciblé
      const updatedDays = week.days.map((day) =>
        day.id === dayId ? { ...day, ...copiedDayData, id: day.id } : day
      );

      // Retourne la semaine mise à jour avec les jours modifiés
      return { ...week, days: updatedDays };
    });

    setPreparedData(newData);
    console.log(`Contenu collé dans le jour ${dayId}.`);
  };

  useEffect(() => {
    const fetchData = async () => {
      const year = getCurrentYear();
      try {
        // Assurez-vous que les fonctions renvoient directement un tableau ou extrayez le tableau comme nécessaire
        const daysResponse = await scheduleGetAllDays(year);
        console.log("daysResponse", daysResponse);

        const preparedData = prepareDataForDisplay(daysResponse);
        setPreparedData(preparedData);
      } catch (error) {
        console.error("Erreur lors de la récupération des horaires:", error);
      }
    };

    const fetchDefaultData = async () => {
      try {
        // Assurez-vous que les fonctions renvoient directement un tableau ou extrayez le tableau comme nécessaire
        const defaultResponse = await scheduleGetDefaultWeek();
        console.log("daysResponse", defaultResponse);

        const defaultData = prepareDataForDisplay(defaultResponse);
      } catch (error) {
        console.error("Erreur lors de la récupération des horaires:", error);
      }
    };

    fetchData();
    fetchDefaultData();
  }, []);

  const prepareDataForDisplay = (data) => {
    const groupedData = [];
    let currentGroup = [];
    const currentWeekNumber = getWeek(new Date());

    Object.keys(data)
      .sort()
      .forEach((weekNumber) => {
        if (parseInt(weekNumber) >= currentWeekNumber) {
          const week = data[weekNumber];
          const firstDayOfWeek = week[0] ? parseISO(week[0].date) : null;
          const weekNum = firstDayOfWeek ? getWeek(firstDayOfWeek) : null;

          week.forEach((day, index) => {
            if (index < 7) {
              currentGroup.push(day);
            }
          });

          if (currentGroup.length > 0 && weekNum !== null) {
            groupedData.push({ weekNum, days: currentGroup });
            currentGroup = [];
          }
        }
      });

    return groupedData;
  };
  const isValidTimeInput = (type, value) => {
    const time = value.split(":").map(Number); // Convertit la valeur en nombre (ex: '09:00' => [9, 0])
    const timeInMinutes = time[0] * 60 + time[1]; // Convertit l'heure en minutes
  
    switch (type) {
      case "ouverturematin":
        // Vérifier si l'heure saisie ne dépasse pas le maximum autorisé
        const morningOpeningMax = minMaxValues.morningOpeningMax.split(":").map(Number);
        const morningOpeningMaxInMinutes = morningOpeningMax[0] * 60 + morningOpeningMax[1];
        return timeInMinutes <= morningOpeningMaxInMinutes;
  
      case "fermeturematin":
        // Vérifier si l'heure saisie n'est pas inférieure au minimum autorisé
        const morningClosingMin = minMaxValues.morningClosingMin.split(":").map(Number);
        const morningClosingMinInMinutes = morningClosingMin[0] * 60 + morningClosingMin[1];
        return timeInMinutes >= morningClosingMinInMinutes;
  
      case "ouvertureaprès-midi":
        // Vérifier si l'heure saisie ne dépasse pas le maximum autorisé
        const afternoonOpeningMax = minMaxValues.afternoonOpeningMax.split(":").map(Number);
        const afternoonOpeningMaxInMinutes = afternoonOpeningMax[0] * 60 + afternoonOpeningMax[1];
        return timeInMinutes <= afternoonOpeningMaxInMinutes;
  
      case "fermetureaprès-midi":
        // Vérifier si l'heure saisie n'est pas inférieure au minimum autorisé
        const afternoonClosingMin = minMaxValues.afternoonClosingMin.split(":").map(Number);
        const afternoonClosingMinInMinutes = afternoonClosingMin[0] * 60 + afternoonClosingMin[1];
        return timeInMinutes >= afternoonClosingMinInMinutes;
  
      default:
        console.error("Type d'input inconnu:", type);
        return false;
    }
  };
  
  

  const handleInputChange = (event, weekIndex, dayIndex, type) => {
    const newValue = event.target.value !== "" ? event.target.value : null;
    const key = type.toLowerCase().replace(/\s/g, ""); // "Ouverture Matin" devient "ouverturematin"

    // Faire une copie profonde de preparedData pour éviter la mutation directe
    const newData = [...preparedData];

    // Assurer l'accès correct à la propriété 'days' de la semaine concernée et au jour spécifique
    if (
      newData[weekIndex] &&
      newData[weekIndex].days &&
      newData[weekIndex].days[dayIndex]
    ) {
      newData[weekIndex].days[dayIndex][key] = newValue;
      setPreparedData(newData);
    } else {
      console.error(
        "Tentative de mise à jour d'un jour non existant dans la semaine spécifiée."
      );
    }
  };

  const handleInputBlur = (event, weekIndex, dayIndex, header) => {
    const newValue = event.target.value;
    const key = header.toLowerCase().replace(/\s/g, "");

    // Faire une copie profonde de preparedData pour éviter la mutation directe
    const newData = [...preparedData];

    // Vérifier si le jour spécifié existe, ensuite mettre à jour sa valeur
    if (newData[weekIndex] && newData[weekIndex].days[dayIndex]) {
      newData[weekIndex].days[dayIndex][key] = newValue || ""; // Utiliser une chaîne vide si la valeur est falsy
      setPreparedData(newData);
    } else {
      console.error("Tentative de mise à jour d'un jour non existant.");
    }
  };

  const openDaysInWeek = (weekIndex) => {
    const newData = [...preparedData];

    // Accéder à la propriété `days` pour la semaine concernée
    newData[weekIndex].days = newData[weekIndex].days.map((day) => ({
      ...day,
      isOpen: true, // Ferme le jour
      morningOpening: "", // Réinitialise les horaires d'ouverture et de fermeture
      morningClosing: "",
      afternoonOpening: "",
      afternoonClosing: "",
    }));

    // Met à jour l'état du composant avec les nouvelles données
    setPreparedData(newData);

    // Si nécessaire, mettez à jour les données sur le serveur ici
  };

  const copyWeekContent = (weekIndex) => {
    const weekToCopy = preparedData[weekIndex];
    if (weekToCopy) {
      // Exclure la propriété 'date' de chaque jour lors de la copie
      const daysWithoutDate = weekToCopy.days.map((day) => {
        const { date, ...dayWithoutDate } = day;
        return dayWithoutDate;
      });

      // Stocker les données copiées sans les dates
      setCopiedWeekData({ ...weekToCopy, days: daysWithoutDate });
      console.log(
        `Contenu de la semaine ${weekToCopy.weekNum} copié, à l'exception des dates.`
      );
    }
  };

  const pasteStoredWeekContent = (weekIndex) => {
    if (!copiedWeekData) {
      console.log("Aucun contenu de semaine à coller.");
      return;
    }

    const newData = [...preparedData];
    const targetWeek = newData[weekIndex];

    // Fusionner les données copiées avec les jours cibles tout en conservant les dates originales
    const mergedDays = targetWeek.days.map((day, index) => {
      if (copiedWeekData.days[index]) {
        return { ...day, ...copiedWeekData.days[index], date: day.date };
      }
      return day;
    });

    newData[weekIndex] = { ...targetWeek, days: mergedDays };
    setPreparedData(newData);
    console.log(
      `Contenu de la semaine collé dans la semaine ${newData[weekIndex].weekNum}, en conservant les dates originales.`
    );
  };

  const closeDaysInWeek = (weekIndex) => {
    const newData = [...preparedData];

    // Accéder à la propriété `days` pour la semaine concernée
    newData[weekIndex].days = newData[weekIndex].days.map((day) => ({
      ...day,
      isOpen: false, // Ferme le jour
      morningOpening: "", // Réinitialise les horaires d'ouverture et de fermeture
      morningClosing: "",
      afternoonOpening: "",
      afternoonClosing: "",
    }));

    // Met à jour l'état du composant avec les nouvelles données
    setPreparedData(newData);

    // Si nécessaire, mettez à jour les données sur le serveur ici
  };

  const rowsHeaders = [
    "Date",
    "État",
    "Ouverture Matin",
    "Fermeture Matin",
    "Ouverture Après-midi",
    "Fermeture Après-midi",
    `Actions`,
  ];

  return (
    <>
      <Container>
        {preparedData.map((weekData, index) => (
          <>
            <div>
              <h2
                key={`week-title-${index}`}
                className="text-light mt-5"
              >
                Semaine {weekData.weekNum}
              </h2>
              <Button
                variant="secondary"
                onClick={() => copyWeekContent(index)}
              >
                Copier
              </Button>{" "}
              <Button
                variant="secondary"
                onClick={() => pasteStoredWeekContent(index)}
              >
                Coller
              </Button>{" "}
              <Button
                variant="secondary"
                onClick={() => openDaysInWeek(index)}
              >
                Ouvrir la semaine
              </Button>
              <Button
                variant="secondary"
                onClick={() => closeDaysInWeek(index)}
              >
                Fermer la semaine
              </Button>
            </div>

            <Table
              key={`table-${index}`}
              striped
              bordered
              hover
              className="text-center mt-4"
              responsive
            >
              <tbody>
                {rowsHeaders.map((header, headerIndex) => (
                  <tr key={headerIndex}>
                    <th>{header}</th>
                    {weekData.days.map((day, dayIndex) => {
                      let cellContent;
                      switch (header) {
                        case "Date":
                          const date = parseISO(day.date); // Supposons que vous avez déjà importé parseISO et format
                          cellContent = format(date, "EEEE dd MMMM yyyy", {
                            locale: fr,
                          });
                          break;
                        case "État":
                          return (
                            <td key={`${headerIndex}-${dayIndex}`}>
                              <Button
                                onClick={() => toggleDayState(index, dayIndex)}
                                style={{
                                  backgroundColor: day.isOpen ? "green" : "red",
                                  color: "white",
                                }}
                              >
                                {day.isOpen ? "Ouvert" : "Fermé"}
                              </Button>
                            </td>
                          );
                        case "Ouverture Matin":
                        case "Fermeture Matin":
                        case "Ouverture Après-midi":
                        case "Fermeture Après-midi":
                          cellContent = (
                            <input
                              key={`input-${dayIndex}-${
                                day.isOpen ? "open" : "closed"
                              }`}
                              type="time"
                              value={
                                day[header.toLowerCase().replace(/\s/g, "")] ||
                                ""
                              }
                              onChange={(e) =>
                                handleInputChange(e, index, dayIndex, header)
                              }
                              onBlur={(e) =>
                                handleInputBlur(e, index, dayIndex, header)
                              }
                              disabled={!day.isOpen}
                              style={{
                                backgroundColor: day.isOpen
                                  ? "lightyellow"
                                  : "lightgrey",
                              }}
                            />
                          );
                          break;
                        case "Actions":
                          cellContent = (
                            <div>
                              <Button
                                variant="secondary"
                                onClick={() => pasteStoredDayContent(day.id)}
                              >
                                Coller
                              </Button>{" "}
                              <Button
                                variant="secondary"
                                onClick={() => copyDayContent(day.id)}
                              >
                                Copier
                              </Button>
                            </div>
                          );
                          break;

                        default:
                          cellContent = "N/A";
                      }
                      return (
                        <td key={`${headerIndex}-${dayIndex}`}>
                          {cellContent}
                        </td>
                      );
                    })}
                  </tr>
                ))}
              </tbody>
            </Table>
          </>
        ))}
      </Container>
    </>
  );
};

export default ScheduleManagement;
