import React, { useState, useEffect } from "react";
import { Button, Form } from "react-bootstrap";
import Select from "react-select";
import { capitalizeFirstLetter } from "../../../../functions/capitalizeFirstLetter";

// Renommez cette variable pour éviter les conflits
let initialVehicleTypes = [
 // { value: "id", label: "VehicleType name" },
];

export const VehicleTypeForm = () => {

  const [selectedVehicleType, setSelectedVehicleType] = useState(null);
  const [vehicleTypes, setVehicleTypes] = useState(initialVehicleTypes); // Utilisez le nom renommé ici

  const fetchVehicleTypes = async () => {
    try {
      const response = await fetch("http://localhost:5001/api/vehicleTypes");
      const data = await response.json();
  
      let seenTypes = {}; // Créez un objet pour suivre les types déjà vus
      const transformedVehicleTypes = data.reduce((unique, vehicleType) => {
        const typeName = vehicleType.vehicle_type_name.toLowerCase(); // Convertissez en minuscules pour la comparaison
  
        if (!seenTypes[typeName]) {
          seenTypes[typeName] = true; // Marquez ce type comme vu
          unique.push({
            value: vehicleType.id.toString(),
            label: capitalizeFirstLetter(vehicleType.vehicle_type_name),
          });
        }
        return unique;
      }, []);
  
      setVehicleTypes(transformedVehicleTypes); // Utilisez la liste filtrée
    } catch (error) {
      console.error("Error fetching vehicle types:", error);
    }
  };
  fetchVehicleTypes();
  

  return (
    <>
      <Form.Label>
        <h3 className="key-info-title">Types :</h3>
      </Form.Label>
      <Select
        value={selectedVehicleType}
        onChange={setSelectedVehicleType}
        options={vehicleTypes}
        isSearchable
      />
    </>
  );
};

export default VehicleTypeForm;
