import React, { useState, useEffect } from "react";
import Table from "react-bootstrap/Table";
import { scheduleGetAllDays } from "../../../serverRelated/ApiRequest";

const getCurrentYear = () => {
  const currentDate = new Date();
  return currentDate.getFullYear();
};

export const ScheduleManagement = () => {
  const [preparedData, setPreparedData] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      const year = getCurrentYear();
      try {
        // Assurez-vous que les fonctions renvoient directement un tableau ou extrayez le tableau comme nécessaire
        const daysResponse = await scheduleGetAllDays(year);
      
         daysResponse = Array.isArray(daysResponse);
        const preparedData = prepareDataForDisplay(daysResponse);
        setPreparedData(preparedData);
      } catch (error) {
        console.error("Erreur lors de la récupération des horaires:", error);
      }
    };

    fetchData();
  }, []);

  const prepareDataForDisplay = (data) => {
    const groupedData = [];
    let currentGroup = [];

    data.forEach((day) => {
      currentGroup.push(day);
      if (currentGroup.length === 7) {
        groupedData.push(currentGroup);
        currentGroup = [];
      }
    });

    if (currentGroup.length > 0) {
      groupedData.push(currentGroup);
    }

    return groupedData;
  };

  const rowsHeaders = [
    "Date",
    "Ouverture Matin",
    "Fermeture Matin",
    "Ouverture Après-midi",
    "Fermeture Après-midi",
  ];

  return (
    <>
      {preparedData.map((week, index) => (
        <Table key={index} striped bordered hover>
          <tbody>
          {rowsHeaders.map((header, headerIndex) => (
  <tr key={headerIndex}>
    <th>{header}</th>
    {week.map((day, dayIndex) => {
      let cellContent;
      switch (header) {
        case "Date":
          cellContent = day.date;
          break;
        case "Ouverture Matin":
          cellContent = day.morningOpening ? day.morningOpening : "Fermé";
          break;
        case "Fermeture Matin":
          cellContent = day.morningClosing ? day.morningClosing : "Fermé";
          break;
        case "Ouverture Après-midi":
          cellContent = day.afternoonOpening ? day.afternoonOpening : "Fermé";
          break;
        case "Fermeture Après-midi":
          cellContent = day.afternoonClosing ? day.afternoonClosing : "Fermé";
          break;
        default:
          cellContent = "N/A";
      }
      return <td key={dayIndex}>{cellContent}</td>;
    })}
  </tr>
))}

          </tbody>
        </Table>
      ))}
    </>
  );
};

export default ScheduleManagement;
