import React, { useState, useEffect } from "react";
import { Carousel, Container, Image } from "react-bootstrap";
import { Row, Col } from "react-bootstrap";
import IconsKeyInfo from "../components/general/VehicleDetailsIcons.js";
import StickyDetails from "../components/vehicleRelated/vehicleDetails/StickyDetails.js";
import { useParams } from "react-router-dom";
import Form from "react-bootstrap/Form";
import { handleDateDifference } from "../functions/handleDateDifference.js";
import { capitalizeFirstLetter } from "../functions/capitalizeFirstLetter.js";
import BrandForm from "../components/vehicleRelated/vehicleForm/keyInfosForm/BrandForm.js";
import ModelForm from "../components/vehicleRelated/vehicleForm/keyInfosForm/ModelForm.js";
import FuelForm from "../components/vehicleRelated/vehicleForm/keyInfosForm/FuelForm.js";
import TransmissionForm from "../components/vehicleRelated/vehicleForm/keyInfosForm/TransmissionForm.js";
import ColorForm from "../components/vehicleRelated/vehicleForm/keyInfosForm/ColorForm.js";
import VehicleTypeForm from "../components/vehicleRelated/vehicleForm/keyInfosForm/VehicleTypeForm.js";
import OptionsForm from "../components/vehicleRelated/vehicleForm/keyInfosForm/OptionsForm.js";
import { FormGroup } from "react-bootstrap";
  import { fetchVehicleModelData } from "../serverRelated/ApiRequest.js";

const carouselStyle = {
  Width: "720px", // Largeur maximale de l'image
  Height: "400px", // Hauteur maximale de l'image
  margin: "auto", // Centre le carrousel si l'écran est plus large
};
const carouselImagesStyle = {
  width: "100%", // Remplir la largeur du cadre
  height: "100%", // Remplir la hauteur du cadre
  objectFit: "cover", // Agrandir ou réduire l'image pour couvrir le cadre
  objectPosition: "center", // Centrer l'image dans le cadre
};

export const VehicleDetailsForm = () => {

  
  const [vehicleDetails, setVehicleDetails] = useState({});
  const [vehicleModel, setVehicleModel] = useState({});
  const [index, setIndex] = useState(0);
  const [selectedModel, setSelectedModel] = useState(null);
  const [selectedTransmission, setSelectedTransmission] = useState(null);
  const [selectedBrand, setSelectedBrand] = useState(null);
  
  const [vehicleData, setVehicleData] = useState({
    id: 1,
    VehicleModel: {
      Brand: {
        id: '', 
        name: ''
      }
    }
  });
  const handleSelect = (selectedIndex, e) => {
    setIndex(selectedIndex);
  };

  const handleTransmissionChange = (transmissionId) => {
    setSelectedTransmission(transmissionId);
  };
  
const handleBrandChange = (selectedBrand) => {
  setVehicleData(prevData => ({
    ...prevData,
    VehicleModel: {
      ...prevData.VehicleModel,
      Brand: {
        id: selectedBrand.value, // Assurez-vous que 'value' est l'ID de la marque
        name: selectedBrand.label
      }
    }
  }));
};


  return (
    <>
      <Container
        style={{ position: "relative", width: "39%", height: "2000px" }}
      >
        <Carousel
          activeIndex={index}
          onSelect={handleSelect}
          fade
          style={carouselStyle}
        >
          {vehicleDetails.photoUrls?.map((url, idx) => (
            <Carousel.Item
              key={idx}
              className="bg-dark"
            >
              <Image
                className="d-block"
                src={url}
                alt={`Slide ${idx + 1}`}
                fluid
                style={carouselImagesStyle}
              />
            </Carousel.Item>
          ))}
        </Carousel>

        <div className="d-flex justify-content-center mt-0 ">
          {vehicleDetails.photoUrls?.map((thumbnailUrl, idx) => (
            <Image
              key={`thumb-${idx}`}
              src={thumbnailUrl}
              alt={`Thumbnail ${idx + 1}`}
              className={`img-thumbnail ${
                index === idx ? "active-carousel-thumbnail" : ""
              } m-1 bg-transparent`}
              onClick={() => setIndex(idx)}
              style={{
                cursor: "pointer",
                maxWidth: "100px",
                maxHeight: "100px",
                marginRight: "4px",
                border: "none",
                outline: "none",
                borderRadius: "8px",
              }}
              thumbnail
            />
          ))}
        </div>
      


        <div className="d-flex justify-content-center mt-2">
          <Container className="p-0">
            <div className="detail-panel-wrapper px-5 py-3 mb-3">
              <h2 className="details-title pb-4">Informations clès</h2>

              <Row id="key-info-wrapper">
                <Col
                  xs={12}
                  md={6}
                  className="px-4"
                >
                  <div className="d-flex align-items-top">
                    <IconsKeyInfo.Factory className="me-2 key-icons" />
                    <div className="px-2">
                      <h3 className="key-info-title">Marque et modèle :</h3>
                      <p className="key-info">
                        {" "}
                        <BrandForm onBrandChange={handleBrandChange}/> - <ModelForm/>                      </p>
                    </div>
                  </div>

                  <div className="d-flex align-items-top">
                    <IconsKeyInfo.Key className="me-2 key-icons" />
                    <div className="px-2">
                    <FormGroup>
                        <Form.Label>
                          <h3 className="key-info-title">
                            Mise en circulation :
                          </h3>
                        </Form.Label>
                        <Form.Control
                          type="number"
                          placeholder="Année de mise en circulation"
                        />
                      </FormGroup>
                    </div>
                  </div>

                  <div className="d-flex align-items-top">
                    <IconsKeyInfo.Mileage className="me-2 key-icons" />
                    <div className="px-2">
                    <FormGroup>
                        <Form.Label>
                          <h3 className="key-info-title">Kilomètrage :</h3>
                        </Form.Label>
                        <Form.Control
                          type="number"
                          placeholder="Kilometrage"
                          id="mileageForm"
                        />
                      </FormGroup>
                    </div>
                  </div>

                  <div className="d-flex align-items-top">
                    <IconsKeyInfo.Fuel className="me-2 key-icons" />
                    <div className="px-2">
                      <h3 className="key-info-title">Carburant :</h3>
                     <FuelForm/>
                    </div>
                  </div>

                  <div className="d-flex align-items-top">
                    <IconsKeyInfo.Transmission className="me-2 key-icons" />
                    <div className="px-2">
                      <h3 className="key-info-title">Transmission :</h3>
                     <TransmissionForm onTransmissionChange={handleTransmissionChange}/>
                    </div>
                  </div>
                </Col>

                <Col
                  xs={12}
                  md={6}
                  className="px-4"
                >
                  <div className="d-flex align-items-top">
                    <IconsKeyInfo.Stars className="me-2 key-icons" />
                    <div className="px-2">
                      <h3 className="key-info-title">Types :</h3>
                      <VehicleTypeForm/>
                    </div>
                  </div>

                  <div className="d-flex align-items-top">
                    <IconsKeyInfo.Painting className="me-2 key-icons" />
                    <div className="px-2">
                      <h3 className="key-info-title">Peinture :</h3>
                      <ColorForm/>
                    </div>
                  </div>

                  <div className="d-flex align-items-top">
                    <IconsKeyInfo.HorsePower className="me-2 key-icons" />
                    <div className="px-2">
                    <FormGroup>
                        <Form.Label>
                          <h3 className="key-info-title">Chevaux Fiscaux :</h3>
                        </Form.Label>
                        <Form.Control
                          type="number"
                          placeholder="Chevaux Fiscaux "
                          id="tax_horsepower"
                        />
                      </FormGroup>
                    </div>
                  </div>

                  <div className="d-flex align-items-top">
                    <IconsKeyInfo.Door className="me-2 key-icons" />
                    <div className="px-2">
                   
                          <h3 className="key-info-title">Portes :</h3>
                          <p className="key-info"></p>
                      
      
                    </div>
                  </div>

                  <div className="d-flex align-items-top">
                    <IconsKeyInfo.Seat className="me-2 key-icons" />
                    <div className="px-2">
                      <h3 className="key-info-title">Sièges :</h3>
                      <p className="key-info"></p>
                    </div>
                  </div>
                </Col>
              </Row>
            </div>
            <div className="detail-panel-wrapper px-5 py-3 mb-3">
              <h2 className="details-title pb-4">Options et équipements</h2>


              <OptionsForm/>
              <Row>
                <Col
                  xs={12}
                  md={6}
                  className="px-4"
                >
                  <div className="d-flex align-items-top"></div>
                </Col>

                <Col
                  xs={12}
                  md={6}
                  className="px-4"
                >
                  <div className="d-flex align-items-top"></div>
                </Col>
              </Row>
            </div>
          </Container>
        </div>
      
      </Container>
    </>
  );
};

export default VehicleDetailsForm;
