import React, { useState } from "react";
import { Carousel, Container, Image } from "react-bootstrap";
import { Row, Col } from 'react-bootstrap';
import IconsKeyInfo from '../components/vehicleRelated/vehicleDetails/VehicleDetailsIcons.js';

const imageUrls = [
  "https://placehold.co/720x400?text=Vehicle+Image",
  "https://placehold.co/720x400?text=Vehicle+Image2",
  "https://placehold.co/720x400?text=Vehicle+Image3",
  "https://placehold.co/720x400?text=Vehicle+Image4",
  "https://placehold.co/720x400?text=Vehicle+Image5",
];

const carouselStyle = {
  maxWidth: "720px", // Largeur maximale de l'image
  maxHeight: "400px", // Hauteur maximale de l'image
  margin: "auto", // Centre le carrousel si l'Ã©cran est plus large
};

export const VehicleDetails = () => {
  const [index, setIndex] = useState(0);

  const handleSelect = (selectedIndex, e) => {
    setIndex(selectedIndex);
  };

  return (
    <Container  style={{width: '39%'}}>
      <Carousel
        activeIndex={index}
        onSelect={handleSelect}
        fade
        style={carouselStyle}
      >
        {imageUrls.map((url, idx) => (
          <Carousel.Item key={idx}>
            <Image
              className="d-block w-100"
              src={url}
              alt={`Slide ${idx + 1}`}
              fluid
            />
          </Carousel.Item>
        ))}
      </Carousel>

      <div className="d-flex justify-content-center mt-2 ">
        {imageUrls.map((thumbnailUrl, idx) => (
          <Image
            key={`thumb-${idx}`}
            src={thumbnailUrl}
            alt={`Thumbnail ${idx + 1}`}
            className={`img-thumbnail ${index === idx ? "active-carousel-thumbnail" : ""} m-1 bg-transparent`}
            onClick={() => setIndex(idx)}
            style={{
              cursor: "pointer",
              maxWidth: "100px",
              maxHeight: "100px",
              marginRight: "4px",
              border: "none",
              outline: "none",
              borderRadius: '8px',
            }}
            thumbnail
          />
        ))}
      </div>

      <div className="d-flex justify-content-center mt-2">
        <Container>
          <div className="detail-panel-wrapper px-5 py-3 mb-3">
            <h2 className="details-title pb-4">Informations clÃ¨s</h2>
            
      <Row>
        <Col xs={12} md={6} className="px-4">
        <div>
        <IconsKeyInfo.Key/>
        <h3 className="key-info-title">Mise en circulation :</h3>
        <p className="key-info">2014</p>
        </div>
        <h3 className="key-info-title">KilomÃ¨trage :</h3>
        <p className="key-info">354 541</p>

        <div>
        <IconsKeyInfo.Fuel/>
        <h3 className="key-info-title">Carburant :</h3>
        <p className="key-info">Diesel</p>
        </div>

        <div>
        <IconsKeyInfo.Transmission className/>
        <h3 className="key-info-title">Transmission :</h3>
        <p className="key-info">Automatic 6</p>
        </div>

 
        </Col>
  
        <Col xs={12} md={6} className="px-4">
        <div>
        <IconsKeyInfo.Stars/>
        <h3 className="key-info-title">Types :</h3>
        <p className="key-info">Berline</p>
        </div>
        <div>
        <IconsKeyInfo.Painting/>
        <h3 className="key-info-title">Peinture :</h3>
        <p className="key-info">Rouge</p>
        </div>
        <div>
        <IconsKeyInfo.Door/>
        <h3 className="key-info-title">Portes :</h3>
        <p className="key-info">5</p>
        </div>
        <div>
        <IconsKeyInfo.Seat/>
        <h3 className="key-info-title">SiÃ¨ges :</h3>
        <p className="key-info">4</p>
        </div>

        </Col>
      </Row>


           

          

          </div>
          <div className="detail-panel-wrapper p-5">
            <h2 className="details-title">Options et Equipements</h2>
            
     

            <h3>Types :</h3>
            <h3>Peinture :</h3>
            <h3>Portes :</h3>

          </div>
        </Container>
      </div>
    </Container>
  );
};

export default VehicleDetails;
