import { Modal, Button, Form } from "react-bootstrap";
import CreatableSelect from "react-select/creatable";
import { useState, useEffect } from "react";
import { fetchVehicles} from "../../../serverRelated/";
import { capitalizeFirstLetter } from "../../../functions/capitalizeFirstLetter";

export const DeleteVehicleModal =  ({ show, onHide }) => {
  const [selectedVehicle, setSelectedVehicle] = useState(null);
  const [vehicles, setVehicles] = useState([]); // Remplacer par un tableau vide comme valeur initiale

  useEffect(() => {
    const handleFetchVehicles = async () => {
      try {
        const response = await fetchVehicles();
        const data = await response.json();

        const transformedVehicles = data.map((vehicle) => ({
          value: vehicle.id.toString(),
          label: `${vehicle.id} - ${capitalizeFirstLetter(vehicle.VehicleModel.Brand.name)} ${capitalizeFirstLetter(vehicle.VehicleModel.name)}`,
        }));

        setVehicles(transformedVehicles);
      } catch (error) {
        console.error("Error fetching vehicles:", error);
      }
    };

    handleFetchVehicles(); // Appeler la fonction pour charger les données
  }, []);

  const handleDeleteVehicle = async () => {
    if (selectedVehicle) {
      try {
        // Ici, insérez la logique pour supprimer le véhicule
        // Par exemple: await deleteVehicle(selectedVehicle.value);
        console.log("Suppression du véhicule:", selectedVehicle);
        // Après suppression, rafraîchir la liste des véhicules ou fermer le modal
        onHide();
      } catch (error) {
        console.error("Error deleting vehicle:", error);
      }
    } else {
      console.error("Aucun véhicule sélectionné pour la suppression");
    }
  };

  return (
    <Modal show={show} onHide={onHide}>
      <Modal.Header closeButton>
        <Modal.Title>Supprimer un véhicule</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <Form.Label>
          <h3 className="key-info-title">Choisissez un véhicule :</h3>
        </Form.Label>
        <CreatableSelect
          isClearable
          isSearchable
          onChange={setSelectedVehicle}
          options={vehicles}
        />
      </Modal.Body>
      <Modal.Footer>
        <Button variant="primary" onClick={handleDeleteVehicle}>Supprimer</Button>
        <Button variant="secondary" onClick={onHide}>Annuler</Button>
      </Modal.Footer>
    </Modal>
  );
};

export default DeleteVehicleModal;
